/// <summary>
/// Lookup of the coupon barcode setup identifier.
/// </summary>
/// <param name = "_formControl">the caller control.</param>
/// <param name = "_filterStr">the filter string.</param>
public void lookup(FormControl _formControl, str _filterStr)
{
    SysTableLookup              sysTableLookup = SysTableLookup::newParameters(tableNum(BarcodeSetup), _formControl);
    Query                       query = new Query();
    QueryBuildDataSource        queryBuildDataSourceBarcode;
    QueryBuildDataSource        queryBuildDataSourceMask ;
    QueryBuildRange             queryBuildRangeBarcode;

    //Add fields to display
    sysTableLookup.addLookupfield(fieldNum(BarcodeSetup, barcodeSetupId));
    sysTableLookup.addLookupfield(fieldNum(BarcodeSetup, Description));
    sysTableLookup.addLookupfield(fieldNum(BarcodeSetup, barcodeType));
    sysTableLookup.addLookupfield(fieldNum(BarcodeSetup, FontSize));
    sysTableLookup.addLookupfield(fieldNum(BarcodeSetup, RetailBarcodeMask));

    //Add BarcodeSetup datasource
    queryBuildDataSourceBarcode = query.addDataSource(tableNum(BarcodeSetup));
    queryBuildRangeBarcode = queryBuildDataSourceBarcode.addRange(fieldNum(BarcodeSetup, RetailBarcodeMask));
    queryBuildRangeBarcode.value(SysQuery::valueNotEmptyString());

    //Add RetailBarcodeMaskTable datasource. Filter the query based on MaskId == Coupon
    queryBuildDataSourceMask = queryBuildDataSourceBarcode.addDataSource(tableNum(RetailBarcodeMaskTable));
    queryBuildDataSourceMask.addLink(fieldNum(BarcodeSetup, RetailBarcodeMask), fieldNum(RetailBarcodeMaskTable, Mask));
    queryBuildDataSourceMask.addRange(fieldNum(RetailBarcodeMaskTable, Type)).value(enum2str(RetailBarcodeMaskTypeBase::Coupon));

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

