void clicked()
{
    super();

    if (Box::yesNo("@Commerce:COMM490352", DialogButton::No, "@RET5057") == DialogButton::Yes)
    {
        // Set default language/region if SMB mode is enabled
        if (RetailSMB::IsRetailSMBEnabled())
        {
            RetailSMBSeedGenerator::UpdateSystemParameters();
            RetailSMBSeedGenerator::CreateNumberSequence();
        }

        RetailConnSeedDataGenerator::main(new Args());

        // Run RetailSMbSeedGenerator if SMB mode is enabled
        if (RetailSMB::IsRetailSMBEnabled())
        {
            RetailSMBSeedGeneratorSettings settings = RetailSMBSeedGeneratorSettings::construct();
            settings.parmReDeployChartOfAccounts(false);

            Args args = new Args();
            args.parmObject(settings);

            RetailSMBSeedGenerator::main(args);
        }
    }
}

void clicked()
{
    super();

    // Run RetailSMbSeedGenerator if SMB mode is enabled
    if (RetailSMB::IsRetailSMBEnabled())
    {
        boolean redeployChartOfAccounts;

        // Get the input from user to Re-deploy chart of accounts, if already eixsts in currnecy legal entity.
        if (Ledger::findByLegalEntity(CompanyInfo::find(true).RecId, true).ChartOfAccounts)
        {
            redeployChartOfAccounts = Box::YesNo("@Retail:RetailRedeploymentDefaultChartOfAccounts", DialogButton::No);
        }
        else
        {
            redeployChartOfAccounts = NoYes::Yes;
        }

        RetailSMBSeedGeneratorSettings settings = RetailSMBSeedGeneratorSettings::construct();
        settings.parmReDeployChartOfAccounts(redeployChartOfAccounts);

        Args args = new Args();
        args.parmObject(settings);

        RetailSMBSeedGenerator::main(args);
    }
}

