/// <summary>
/// Lookup method for carry out mode of delivery to populate those mode of deliveries which are not part of pickup modes of delivery.
/// </summary>
public void lookup()
{
    Query query = new Query();
    QueryBuildDataSource qbdsDlvMode, qbdsRetailDlvMode;
    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tablenum(DlvMode), this);
    ;

    // Add fields to be shown in the lookup form.
    sysTableLookup.addLookupfield(fieldnum(DlvMode,Code));
    sysTableLookup.addLookupfield(fieldnum(DlvMode,Txt));

    qbdsDlvMode = query.addDataSource(tablenum(DlvMode));
    qbdsRetailDlvMode = qbdsDlvMode.addDataSource(tableNum(RetailDlvMode));
    qbdsRetailDlvMode.relations(true);
    qbdsRetailDlvMode.joinMode(JoinMode::NoExistsJoin);

    sysTableLookup.parmQuery(query);

    // Perform the lookup.
    sysTableLookup.performFormLookup();
}

