public int selectionChange()
{
    int ret;
    int newValue;
    boolean  ok;

    ret = super();

    if (ret)
    {
        ok = Box::okCancel("@Retail:FulfillmentLocationAssignmentControlModelUpdateConfirm", DialogButton::Cancel) == DialogButton::Ok;
    }

    if (!ok)
    {
        ret = ok;
        this.selection(RetailConfigurationParameters::getFulfillmentLocationAssignmentControlModel());
    }

    newValue = this.selection();

    RetailConfigurationParameters::createOrUpdateFulfillmentLocationControlModel(newValue);
    RetailConfigurationParameters_ds.research();

    return ret;
}

/// <summary>
/// Validates that the customer can change the configuration.
/// </summary>
/// <returns></returns>
public boolean validate()
{
    RetailSharedParameters localRetailSharedParameters;
    boolean ret;

    ret = super();
    int newValue = this.selection();

    if (newValue == RetailFulfillmentLocationAssignmentControlModel::AssignEstimateOfBestLocation)
    {
        select ProductAvailabilityJobPolicy
            from localRetailSharedParameters;

        // If we try to change our fulfillment policy to calculate the best estimated location (not recommended), we must use RetailInventAvailability.
        if (localRetailSharedParameters && localRetailSharedParameters.ProductAvailabilityJobPolicy == RetailProductAvailabilityJobControlModel::PopulateRetailProductWarehouseInventoryTable)
        {
            ret = false;
            error("@RETAIL:FulfillmentLocationAssignmentCannotCalculateOnNewTable");
        }
    }

    return ret;
}

