public Common lookupReference()
{
    SysReferenceTableLookup sysTableLookup;
    Query query;
    QueryBuildDataSource nsTableDS;

    // Look up NumberSequenceTable
    sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(NumberSequenceTable), this, true);
    sysTableLookup.addLookupfield(fieldNum(NumberSequenceTable, NumberSequence));
    sysTableLookup.addLookupfield(fieldNum(NumberSequenceTable, Txt));

    query = new Query();
    nsTableDS = query.addDataSource(tableNum(NumberSequenceTable));

    // Filter by NumberSequenceScope
    nsTableDS.addRange(fieldNum(NumberSequenceTable, NumberSequenceScope)).value(queryValue(numberSequenceReference.NumberSequenceScope));

    sysTableLookup.parmQuery(query);

    return sysTableLookup.performFormLookup();
}

