/// <summary>
/// If checked, set the Product recommendations model information on the Retail parameters table for the current company.
/// </summary>
/// <returns>A value indicating whether the process succeeded.</returns>
public boolean modified()
{
    boolean ret;
    boolean successfullyRetrievedRecommendationModels = false;
    CLRObject ex;

    if (!this.@checked())
    {
        RetailParameters.EnableProductRecommendations = NoYes::No;
        RetailParameters.RecommendationsModelId = '';
        RetailParameters.RecommendationsModelName = '';
        RetailParameters.RecommendationsActiveBuildType = '';
        RetailParameters.RecommendationsActiveBuildCreatedDate = '';
        RetailParameters.FbtModelId = '';
        RetailParameters.FbtModelName = '';
        RetailParameters.FbtActiveBuildType = '';
        RetailParameters.FbtActiveBuildCreatedDate = '';
        RetailParameters.update();
    }
    else
    {
        // Retrieve the account information for the Cortana Analytics service.
        container cortanaAnalyticsAppConfiguration = RetailTransactionServiceRecommendations::GetCortanaAnalyticsAppConfiguration(RetailTransactionServiceRecommendations::CortanaAnalyticsApplicationName);
        if (conPeek(cortanaAnalyticsAppConfiguration, 1))
        {
            var accountName = conPeek(cortanaAnalyticsAppConfiguration, 3);
            var accountKey = conPeek(cortanaAnalyticsAppConfiguration, 4);

            try
            {
                // Initialize the proxy.
                Microsoft.CortanaIntelligence.RecommendationsServiceClient.ModelAuth modelAuth =
                    new Microsoft.CortanaIntelligence.RecommendationsServiceClient.ModelAuth(accountName, accountKey);
                Microsoft.CortanaIntelligence.RecommendationsServiceClient.RecommendationsClient api =
                    new Microsoft.CortanaIntelligence.RecommendationsServiceClient.RecommendationsClient(modelAuth);

                boolean foundModels = false;
                var models = api.GetAllModels();

                // Add the active models to the data source used to display the models in the UI.
                System.Collections.IEnumerator modelEnumerator = models.GetEnumerator();
                while (modelEnumerator.MoveNext())
                {
                    foundModels = true;
                    Microsoft.CortanaIntelligence.RecommendationsServiceClient.CognitiveServices.Models.ModelInfo currentModel = modelEnumerator.Current;

                    Microsoft.CortanaIntelligence.RecommendationsServiceClient.CognitiveServices.Models.BuildInfo activeBuild =
                        api.GetBuildById(currentModel.Id, currentModel.ActiveBuildId);

                    str buildDate = activeBuild.ModifiedDateTime != null ? activeBuild.ModifiedDateTime : activeBuild.EndDateTime;

                    if (activeBuild.Type == 'Fbt')
                    {
                        RetailParameters.FbtModelId = currentModel.Id;
                        RetailParameters.FbtModelName = currentModel.Name;
                        RetailParameters.FbtActiveBuildType = 'Frequently bought together';
                        RetailParameters.FbtActiveBuildCreatedDate = buildDate;
                    }
                    else
                    {
                        RetailParameters.RecommendationsModelId = currentModel.Id;
                        RetailParameters.RecommendationsModelName = currentModel.Name;
                        RetailParameters.RecommendationsActiveBuildType = activeBuild.Type;
                        RetailParameters.RecommendationsActiveBuildCreatedDate = buildDate;
                    }

                    successfullyRetrievedRecommendationModels = true;
                }

                if (!foundModels)
                {
                    eventSource.EventWriteProductsGenericError('RetailParameters', funcName(), 'No models were found.');
                }
            }
            catch(Exception::Error)
            {
                eventSource.EventWriteProductsGenericError('RetailParameters', funcName(), 'An error occurred when retrieving the recommendation models.');
                successfullyRetrievedRecommendationModels = false;
            }
            catch(Exception::CLRError)
            {
                ex = CLRInterop::getLastException();
                eventSource.EventWriteProductsGenericClrException('RetailParameters', funcName(), 'inner type unknown');
                successfullyRetrievedRecommendationModels = false;
            }
        }
        else
        {
            Message::Add(MessageSeverity::Warning, "@Retail:ProductRecommendationsUnableToRetrieveCredentials");
            this.@checked(false);
        }

        if (successfullyRetrievedRecommendationModels)
        {
            RetailParameters.EnableProductRecommendations = NoYes::Yes;
            RetailParameters.update();
        }
        else
        {
            Message::Add(MessageSeverity::Warning, "@Retail:ProductRecommendationsErrorRetrievingModels");
            this.@checked(false);
        }
    }

    ret = super();

    return ret && (successfullyRetrievedRecommendationModels || !this.@checked());
}

