/// <summary>
/// Gets the customer search fields.
/// </summary>
public void enter()
{
    super();

    this.ensureRetailTaxRegistrationIdFeatureExposureEnabled();
}

/// <summary>
/// Ensures that the tax registration id feature is enabled for the current legal entity
/// and enables the TaxRegistrationId enum member of <c>RetailCustomerSearchFieldEnum</c> accordingly.
/// </summary>
private void ensureRetailTaxRegistrationIdFeatureExposureEnabled()
{
    if ((SysCountryRegionCode::isLegalEntityInCountryRegion([#isoBR]) && !RetailTaxRegistrationIdFeatureExposure_BR::isEnabled()) ||
        (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]) && !RetailTaxRegistrationIdFeatureExposure_IN::isEnabled()))
    {
        this.delete(enum2str(RetailCustomerSearchFieldEnum::TaxRegistrationId));
    }
}

/// <summary>
/// Handle modify event.
/// </summary>
/// <returns></returns>
public boolean modified()
{
    boolean ret;

    ret = super();

    element.enableDisableCustomerInsightsFields();

    // reset fields.
    ClientelingParameters.CustomerInsightsInstanceId = "";
    ClientelingParameters.CustomerInsightsAlternativeCustomerIdFieldName = "";
    ClientelingParameters.CustomerInsightsCustomerAttribute1 = "";
    ClientelingParameters.CustomerInsightsCustomerAttribute2 = "";
    ClientelingParameters.CustomerInsightsCustomerAttribute3 = "";
    ClientelingParameters.CustomerInsightsAuthorityUri = "";

    if (this.checked())
    {
        // set url in the data source.
        var customerInsightClient = new CustomerInsightsServiceClient(CIApplicationId.valueStr(), CIApplicationSecretName.valueStr());
        ClientelingParameters.CustomerInsightsAuthorityUri = customerInsightClient.GetAuthorityUrl();
    }

    return ret;
}

/// <summary>
/// Validate required fields and credentials.
/// </summary>
/// <returns>Returns true if valid. False if not valid.</returns>
public boolean validate()
{
    CLRObject ex;
    boolean returnValue;
    returnValue = super();

    if (this.checked())
    {
        str applicationId = CIApplicationId.valueStr();
        str secretName = CIApplicationSecretName.valueStr();

        returnValue = strLen(applicationId) > 0 && strLen(secretName) > 0;

        if (!returnValue)
        {
            Error(strFmt("@Retail:CustomerInsightsErrorMissingRequiredField"));
        }
        else
        {
            try
            {
                returnValue = CustomerInsightsServiceClient::VerifyConnection(applicationId, secretName);
            }
            catch(Exception::Error)
            {
                eventSource.EventWriteProductsGenericError('RetailParameters_EnableCIIntegration_validate', funcName(), 'Customer Insights test connection failed:');
                returnValue = false;
            }
            catch(Exception::CLRError)
            {
                ex = CLRInterop::getLastException();
                eventSource.EventWriteProductsGenericClrException('RetailParameters_EnableCIIntegration_validate', funcName(), ex.toString());
                returnValue = false;
            }

            if (!returnValue)
            {
                Error("@Retail:CustomerInsightsErrorFailedToTestConnection");
            }
        }

    }

    return returnValue;
}

