Method.Source:
    [Form]
    public class RetailPosBatchTable extends FormRun
    {
        QueryBuildRange     storeId_qbr;
        QueryBuildRange     terminalId_qbr;
        QueryBuildRange     startDate_qbr;
        QueryBuildRange     endDate_qbr;

}

void setRanges()
{
    ;

    if (storeIdCriteria.text())
    {
        storeId_qbr.value(SysQuery::value(storeIdCriteria.text()));
    }
    else
    {
        storeId_qbr.value(SysQuery::valueUnlimited());
    }

    if (terminalIdCriteria.text())
    {
        terminalId_qbr.value(SysQuery::value(terminalIdCriteria.text()));
    }
    else
    {
        terminalId_qbr.value(SysQuery::valueUnlimited());
    }

    if (batchFromDateCriteria.dateValue())
    {
        startDate_qbr.value(SysQuery::range(batchFromDateCriteria.dateValue(), SysQuery::valueUnlimited()));
    }
    else
    {
        startDate_qbr.value(SysQuery::valueUnlimited());
    }

    if (batchToDateCriteria.dateValue())
    {
        endDate_qbr.value(SysQuery::range(dateNull(), batchToDateCriteria.dateValue()));
    }
    else
    {
        endDate_qbr.value(SysQuery::valueUnlimited());
    }

    retailPosBatchTable_DS.executeQuery();
}

/// <summary>
/// Enables or disables buttons on the shift form.
/// </summary>
public void resetButtons()
{
    AcknowledgeError.enabled(retailPosBatchTable.ErrorCode == RetailEodShiftValidationErrorCode::Error);

    // The button may be visible, but disabled in case it was already marked for a financial statement.
    MarkForFinancialStatement.enabled(RetailPosBatchTable.MarkForFinancialStatement == NoYes::No);
    MarkForFinancialStatement.visible(RetailEodTrickleFeedFeatureExposure::isEnabled() && RetailPosBatchTable.Posted == NoYes::No && RetailPosBatchTable.StatementId == '');
}

public void init()
{
    RetailStatementJour postedStatement;

    super();

    storeId_qbr     = this.query().dataSourceTable(tablenum(RetailPosBatchTable)).addRange(fieldnum(RetailPosBatchTable, StoreId));
    terminalId_qbr  = this.query().dataSourceTable(tablenum(RetailPosBatchTable)).addRange(fieldnum(RetailPosBatchTable, TerminalId));
    startDate_qbr   = this.query().dataSourceTable(tablenum(RetailPosBatchTable)).addRange(fieldnum(RetailPosBatchTable, StartDate));
    endDate_qbr     = this.query().dataSourceTable(tablenum(RetailPosBatchTable)).addRange(fieldnum(RetailPosBatchTable, CloseDate));

    // If caller is Posted Statement, scope query results to the shifts which belong to the statement.
    if (element.args().caller() &&
        element.args().record().TableId == tableNum(RetailStatementJour))
    {
        postedStatement = element.args().record();

        this.query().dataSourceTable(tableNum(RetailPosBatchTable)).addRange(fieldNum(RetailPosBatchTable, StatementId)).value(queryValue(postedStatement.statementId));
    }
}

/// <summary>
/// Sets the fields for any active record.
/// </summary>
/// <returns>An integer value indicating if the active handler executed successfully.</returns>
public int active()
{
    int ret;

    ret = super();

    element.resetButtons();

    return ret;
}

public void init()
{
    super();

    this.query().dataSourceTable(tableNum(RetailXZReportTable)).addLink(fieldNum(RetailPosBatchTable, BatchID), fieldNum(RetailXZReportTable, BatchId));
    this.query().dataSourceTable(tableNum(RetailXZReportTable)).addLink(fieldNum(RetailPosBatchTable, TerminalId), fieldNum(RetailXZReportTable, TerminalId));
    this.query().dataSourceTable(tableNum(RetailXZReportTable)).addLink(fieldNum(RetailPosBatchTable, StoreId), fieldNum(RetailXZReportTable, StoreId));

    this.query().dataSourceTable(tableNum(RetailXZReportTable)).addRange(fieldNum(RetailXZReportTable, ReportType)).value(queryValue(RetailXZReportType::ZReport));
}

/// <summary>
/// Executes the acknowledge error process.
/// </summary>
public void clicked()
{
    super();

    retailPosBatchTable.acknowledgeError();

    retailPosBatchTable_DS.executeQuery();
    retailPosBatchTable_DS.reread();
    retailPosBatchTable_DS.refresh();

    element.resetButtons();
}

/// <summary>
/// Executes the mark for financial statement process.
/// </summary>
public void clicked()
{
    super();

    RetailPosBatchTable.markForFinancialStatement();

    retailPosBatchTable_DS.reread();
    retailPosBatchTable_DS.refresh();

    element.resetButtons();
}

