[Form]
public class RetailPosPermissionGroup extends FormRun
{
    private boolean isRetailHideStoreInventoryUserFlight = RetailHideStoreInventoryUserFlight::instance().isEnabled();

}

public void init()
{
    super();
    //Initialize splitter

    if (isRetailHideStoreInventoryUserFlight)
    {
        RetailPosPermissionGroup_UseHandheld.visible(false);
    }
}

/// <summary>
/// Disable other boolean permissions when manager privileges is turned on.
/// </summary>
private void honorManagerPrivileges()
{
    boolean enableOtherBooleanPermissions = !RetailPosPermissionGroup.managerPrivileges;

    for (int i = 1; i <= Permissions.controlCount(); i ++)
    {
        var permissionControl = Permissions.controlNum(i);
        if (permissionControl == RetailPosPermissionGroup_managerPrivileges)
        {
            continue;
        }

        FormControlType formControlType = SysFormRun::controlType(classIdGet(permissionControl));
        if (formControlType == FormControlType::CheckBox)
        {
            permissionControl.enabled(enableOtherBooleanPermissions);
        }
    }

    for (int j = 1; j <= FiscalRegistration.controlCount(); j++)
    {
        var fiscalPermissionControl = FiscalRegistration.controlNum(j);

        FormControlType formControlType = SysFormRun::controlType(classIdGet(fiscalPermissionControl));
        if (formControlType == FormControlType::CheckBox)
        {
            fiscalPermissionControl.enabled(enableOtherBooleanPermissions);
        }
    }
}

public void init()
        {
            // <GEERU>
            #ISOCountryRegionCodes
            // </GEERU>

            super();

            // <GEERU>
            if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
            {
                this.object(fieldNum(RetailPosPermissionGroup, AllowDifferentPaymentMethodRefunds_RU)).visible(true);
                this.object(fieldNum(RetailPosPermissionGroup, AllowNotSameDateReturn_RU)).visible(true);
                this.object(fieldNum(RetailPosPermissionGroup, AllowReturnOfNonReturnableItem_RU)).visible(true);
                this.object(fieldNum(RetailPosPermissionGroup, AllowSetupLabelPrinter)).visible(true);
            }
            // </GEERU>
        }

/// <summary>
/// When data loaded, if the manager privileges is turned on, grey other boolean permissions.
/// </summary>
/// <returns>True if the datasource is successfully activated, otherwise false.</returns>
public int active()
{
    int ret;

    ret = super();
    element.honorManagerPrivileges();

    if (isRetailHideStoreInventoryUserFlight)
    {
        RetailPosPermissionGroup_UseHandheld.visible(false);
    }

    return ret;
}

/// <summary>
/// When manager privileges turned on, disable other boolean permissions.
/// </summary>
/// <returns>True if the control is successfully modified, otherwise false.</returns>
public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        element.honorManagerPrivileges();
        if (RetailPosPermissionGroup.managerPrivileges == NoYes::Yes)
        {
            Info("@Retail:ManagerPrivilegesCheckFirst");
        }
    }

    return ret;
}

/// <summary>
/// Populates the ReferenceGroup dropdown with Retail operations which have EnableNotifications set to 'Yes'.
/// </summary>
/// <returns></returns>
public Common lookupReference()
{
    SysReferenceTableLookup           sysTableLookup = SysReferenceTableLookup::newParameters(tablenum(RetailOperations), this);
    Query                   query = new Query();
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildRange         queryBuildRange;
    RetailOperations selectedRecord;

    queryBuildDataSource = query.addDataSource(tablenum(RetailOperations));
    queryBuildRange      = queryBuildDataSource.addRange(fieldnum(RetailOperations, EnableNotifications));
    queryBuildRange.value(queryValue(NoYes::Yes));
    sysTableLookup.addLookupfield(fieldnum(RetailOperations, OperationId));
    sysTableLookup.addLookupfield(fieldnum(RetailOperations, OperationName));
    sysTableLookup.parmQuery(query);
    selectedRecord = sysTableLookup.performFormLookup();
    return selectedRecord;
}
