[Form]
 public class RetailPositionPosPermission extends FormRun
{
    private boolean isRetailHideStoreInventoryUserFlight = RetailHideStoreInventoryUserFlight::instance().isEnabled();

}

public void disablePermissions(boolean _valuestr)
{

    PermissionGroup.enabled(_valuestr);
    DetailsGroup.enabled(_valuestr);

    // Hides and shows Notification subscription grids.
    element.design().controlName("NotificationsPermissionGroup").visible(!_valuestr);
    element.design().controlName("NotificationsPosition").visible(_valuestr);

}

public void init()
{
    if (!element.args().caller() || element.args().dataset() != tableNum(HcmWorker))
    {
        throw error("@SYS22539");
    }

    super();

    if (isRetailHideStoreInventoryUserFlight)
    {
        RetailPositionPosPermission_UseHandheld.visible(false);
    }
}

/// <summary>
/// Sets position pos permissions to POS permissions set in Job profile.
/// </summary>
public void setPositionPosPermissions()
{
    HcmPosition hCMPositionloc;
    HcmPositionDetail hcmPositionDetail;
    RetailPosPermissionGroup    retailPosPermissionGrouploc;
    RetailJobPosPermissionGroup retailJobPosPermissionGroup;

    select firstonly RecId
    from hCMPositionloc
    join Job
        from hcmPositionDetail
            where   hCMPositionloc.PositionId  == HcmPosition.PositionId
                &&  hcmPositionDetail.Position == hCMPositionloc.RecId
    join RetailPosPermissionGroup
        from retailJobPosPermissionGroup
            where retailJobPosPermissionGroup.Job == hcmPositionDetail.Job
    join *
        from retailPosPermissionGrouploc
            where retailPosPermissionGrouploc.RecId == retailJobPosPermissionGroup.RetailPosPermissionGroup;

    if (retailPosPermissionGrouploc)
    {
        RetailPositionPosPermission.Position                          = hCMPositionloc.RecId;
        RetailPositionPosPermission.initFromRetailPosPermissionGroup(retailPosPermissionGrouploc);
    }
}

/// <summary>
/// Disable other boolean permissions when manager privileges is turned on.
/// </summary>
private void honorManagerPrivileges()
{
    boolean enableOtherBooleanPermissions = !RetailPositionPosPermission.managerPrivileges;

    for (int i = 1; i <= DetailsGroup.controlCount(); i++)
    {
        var permissionControl = DetailsGroup.controlNum(i);
        if (permissionControl == RetailPositionPosPermission_managerPrivileges)
        {
            continue;
        }

        FormControlType formControlType = SysFormRun::controlType(classIdGet(permissionControl));
        if (formControlType == FormControlType::CheckBox)
        {
            permissionControl.enabled(enableOtherBooleanPermissions);
        }
    }

    for (int j = 1; j <= FiscalRegistration.controlCount(); j++)
    {
        var fiscalPermissionControl = FiscalRegistration.controlNum(j);

        FormControlType formControlType = SysFormRun::controlType(classIdGet(fiscalPermissionControl));
        if (formControlType == FormControlType::CheckBox)
        {
            fiscalPermissionControl.enabled(enableOtherBooleanPermissions);
        }
    }
}

public void executeQuery()
{
    HcmWorker hcmworkerloc;

    super();

    if (element.args().record())
    {
        hcmworkerloc = element.args().record();

        hcmPositionWorkerAssignment_q.dataSourceTable(tableNum(HcmPositionWorkerAssignment)).addRange(fieldNum(HcmPositionWorkerAssignment, Worker)).value(int642str(hcmworkerloc.RecId));

        // Disable any edit if there is no position assignment for the worker
        overridePermission.enabled(hcmPositionWorkerAssignment.RecId > 0);
    }

    RetailPositionPosPermission_ds.reread();
    RetailPositionPosPermission_ds.refresh();

    RetailNotificationSubscriptions_ds.reread();
    RetailNotificationSubscriptions_ds.refresh();

    RetailPositionNotificationSubscriptions_ds.reread();
    RetailPositionNotificationSubscriptions_ds.refresh();
}

public void selectionChanged()
{
    HcmPosition hCMPositionloc;
    RetailPositionPosPermission retailPositionPosPermissionloc;

    super();

    select firstonly PositionId from hCMPositionloc
        where hCMPositionloc.PositionId == HcmPosition.PositionId;

    select firstonly retailPositionPosPermissionloc
        where retailPositionPosPermissionloc.Position == hCMPositionloc.RecId;

    if (retailPositionPosPermissionloc)
    {
        Overridepermission.value(1);
        element.disablePermissions(true);
        RetailPositionPosPermission_ds.reread();
        RetailPositionPosPermission_ds.refresh();

    }
    else
    {
        Overridepermission.value(0);
        element.disablePermissions(false);
        element.setPositionPosPermissions();
        RetailPositionPosPermission_ds.refresh();
    }

    element.honorManagerPrivileges();
}

public void write()
{
    if (Overridepermission.checked() == NoYes::Yes)
    {
        super();
    }
}

