public int active()
{
    int ret;

    ret = super();

    if (Overridepermission.checked() == NoYes::No)
    {
        element.setPositionPosPermissions();
        RetailPositionPosPermission_ds.refresh();
    }

    element.honorManagerPrivileges();

    if (isRetailHideStoreInventoryUserFlight)
    {
        RetailPositionPosPermission_UseHandheld.visible(false);
    }

    return ret;
}

public void modified()
{
    if (Box::yesNo("@RET4286", DialogButton::Yes) == DialogButton::Yes)
    {
        // Pos Permission Group is changed so we must set the position notification subscriptions to match those of the new permission group
        var changedGroup = RetailPositionPosPermission.PosPermissionGroup;

        // Insert position notification subscriptions corresponding with the new pos permission group
        RetailPositionNotificationSubscriptions.populateFromRetailNotificationSubscriptions(RetailPositionPosPermission);

        // Reread RetailPositionPosPermission to avoid Update conflict errors. Store the new pos permission group first.
        var permissionGroupTemp = RetailPositionPosPermission.PosPermissionGroup;
        RetailPositionPosPermission_ds.reread();

        // Set the position pos permission to match the corresponding new pos permission group
        RetailPositionPosPermission.PosPermissionGroup = permissionGroupTemp;
        super();
        RetailPositionPosPermission.update();
        RetailPositionPosPermission_ds.refresh();

        RetailPositionNotificationSubscriptions_ds.executeQuery();
        RetailPositionNotificationSubscriptions_ds.refresh();

        element.honorManagerPrivileges();
        if (RetailPositionPosPermission.managerPrivileges == NoYes::Yes)
        {
            Info("@Retail:ManagerPrivilegesCheckFirst");
        }
    }
    else
    {
        RetailPositionPosPermission retailPositionPosPermissionLocal;
        select firstonly PosPermissionGroup
            from retailPositionPosPermissionLocal
        where retailPositionPosPermissionLocal.Position == RetailPositionPosPermission.Position;

        RetailPositionPosPermission.PosPermissionGroup = retailPositionPosPermissionLocal.PosPermissionGroup;
        RetailPositionPosPermission_ds.refresh();
    }

}

