    if (Overridepermission.checked() == NoYes::Yes)
    {
        super();
    }
}

public boolean modified()
{
    RetailPositionPosPermission retailPositionPosPermissionLocal;
    RetailPositionNotificationSubscriptions    retailPositionNotificationSubscriptionsLocal;
    boolean ret;

    ret = super();

    if (Overridepermission.checked() == NoYes::Yes)
    {
        // If override permissions is set to true, we insert the position pos permission and the position notification subscriptions
        element.disablePermissions(1);
        RetailPositionPosPermission_ds.allowEdit(true);

        RetailPositionPosPermission.insert();
        RetailPositionNotificationSubscriptions.populateFromRetailNotificationSubscriptions(RetailPositionPosPermission);

        RetailPositionNotificationSubscriptions_ds.executeQuery();
        RetailPositionNotificationSubscriptions_ds.refresh();
    }
    else
    {
        // If override permissions is set to false, delete all associated entries in both position tables
        element.disablePermissions(0);

        delete_from retailPositionPosPermissionLocal
            where retailPositionPosPermissionLocal.Position == RetailPositionPosPermission.Position;

        delete_from retailPositionNotificationSubscriptionsLocal
            where retailPositionNotificationSubscriptionsLocal.RetailPositionPosPermission == RetailPositionPosPermission.RecId;

        // Reread RetailPositionPosPermission to avoid Update conflict errors. Store the new pos permission group first.
        var permissionGroupTemp = RetailPositionPosPermission.PosPermissionGroup;
        RetailPositionPosPermission_ds.reread();

        // Set the position pos permission to match the corresponding pos permission group
        RetailPositionPosPermission.PosPermissionGroup = permissionGroupTemp;
        element.setPositionPosPermissions();
        RetailPositionPosPermission_ds.refresh();

        RetailPosPermissionGroup_ds.executeQuery();
        RetailPosPermissionGroup_ds.refresh();

        RetailNotificationSubscriptions_ds.executeQuery();
        RetailNotificationSubscriptions_ds.refresh();
    }

    element.honorManagerPrivileges();

    return ret;
}

/// <summary>
/// When manager privileges turned on, disable other boolean permissions.
/// </summary>
/// <returns>True if the control is successfully modified, otherwise false.</returns>
public boolean modified()
{
    boolean ret;

    ret = super();
    if (ret)
    {
        element.honorManagerPrivileges();
        if (RetailPositionPosPermission.managerPrivileges == NoYes::Yes)
        {
            Info("@Retail:ManagerPrivilegesCheckFirst");
        }
    }

    return ret;
}

