    [Form]
    public class RetailPriceDiscGroup extends FormRun
    {
        boolean               channelEnabled;
        boolean               catalogEnabled;
        boolean               affiliationEnabled;
        boolean               loyaltyEnabled;

}

/// <summary>
    /// initialize form based on menu calls.
    /// </summary>
    public void init()
    {
        QueryBuildRange         rangeModule;
        QueryBuildRange         rangeType;
        QueryBuildRange         rangeIsRetail;
        MenuItemName            callerMenuItemName = element.args().menuItemName();

        super();

        catalogEnabled= false;
        channelEnabled = false;
        affiliationEnabled = false;
        loyaltyEnabled = false;

        if (callerMenuItemName == menuitemDisplayStr(RetailPriceDiscGroupCatalogs))
        {
            catalogEnabled = true;
            element.design().caption("@REX4040243");
            priceDiscGroup_ds.allowCreate(false);
            priceDiscGroup_ds.allowEdit(false);
            priceDiscGroup_ds.allowDelete(false);
        }
        else if (callerMenuItemName == menuitemDisplayStr(RetailPriceDiscGroupChannels))
        {
            channelEnabled = true;
            element.design().caption("@REX4161378");
            priceDiscGroup_ds.allowCreate(false);
            priceDiscGroup_ds.allowEdit(false);
            priceDiscGroup_ds.allowDelete(false);
        }
        else if (callerMenuItemName == menuitemDisplayStr(RetailPriceDiscGroupAffiliations))
        {
            affiliationEnabled = true;
            element.design().caption("@REX4040248");
            priceDiscGroup_ds.allowCreate(false);
            priceDiscGroup_ds.allowEdit(false);
            priceDiscGroup_ds.allowDelete(false);
        }
        else if (callerMenuItemName == menuitemDisplayStr(RetailPriceDiscGroupLoyalty))
        {
            loyaltyEnabled = true;
            element.design().caption("@REX:LoyaltyPriceGroups");
            priceDiscGroup_ds.allowCreate(false);
            priceDiscGroup_ds.allowEdit(false);
            priceDiscGroup_ds.allowDelete(false);
        }
        tabPageAffiliations.visible(affiliationEnabled || loyaltyEnabled);
        tabPageChannels.visible(channelEnabled);
        tabPageCatalogs.visible(catalogEnabled);

        // Retail uses price groups of ModuleInventCustVend::Cust and PriceGroupType::PriceGroup
        rangeModule = priceDiscGroup_ds.query().dataSourceTable(tableNum(PriceDiscGroup)).addRange(fieldNum(PriceDiscGroup, Module));
        rangeModule.value(queryValue(ModuleInventCustVend::Cust));

        rangeType = priceDiscGroup_ds.query().dataSourceTable(tableNum(PriceDiscGroup)).addRange(fieldNum(PriceDiscGroup, Type));
        rangeType.value(queryValue(PriceGroupType::PriceGroup));

        this.resetButtons();
    }

public void init()
{
    MenuItemName            callerMenuItemName = element.args().menuItemName();
    QueryBuildDataSource qbds, qbds1;
    QueryBuildRange qbr;

    super();

    if (callerMenuItemName == menuitemDisplayStr(RetailPriceDiscGroupCatalogs))
    {
        qbds = this.query().dataSourceTable(TableNum(PriceDiscGroup)).addDataSource(tableNum(RetailCatalogPriceGroup));
        qbds.joinMode(JoinMode::ExistsJoin);
        qbds.relations(true);
    }
    else if (callerMenuItemName == menuitemDisplayStr(RetailPriceDiscGroupAffiliations))
    {
        qbds = this.query().dataSourceTable(TableNum(PriceDiscGroup)).addDataSource(tableNum(RetailAffiliationPriceGroup));
        qbds.joinMode(JoinMode::ExistsJoin);
        qbds.relations(true);

        qbds1 = qbds.addDataSource(tableNum(RetailAffiliation));
        qbds1.joinMode(JoinMode::InnerJoin);
        qbds1.relations(true);

        qbr = qbds1.addRange(fieldNum(RetailAffiliation, AffiliationType));
        qbr.value(queryValue(RetailAffiliationType::General));
    }
    else if (callerMenuItemName == menuitemDisplayStr(RetailPriceDiscGroupLoyalty))
    {
        qbds = this.query().dataSourceTable(TableNum(PriceDiscGroup)).addDataSource(tableNum(RetailAffiliationPriceGroup));
        qbds.joinMode(JoinMode::ExistsJoin);
        qbds.relations(true);

        qbds1 = qbds.addDataSource(tableNum(RetailAffiliation));
        qbds1.joinMode(JoinMode::InnerJoin);
        qbds1.relations(true);

        qbr = qbds1.addRange(fieldNum(RetailAffiliation, AffiliationType));
        qbr.value(queryValue(RetailAffiliationType::Loyalty));
    }
    else if (callerMenuItemName == menuitemDisplayStr(RetailPriceDiscGroupChannels))
    {
        qbds = this.query().dataSourceTable(TableNum(PriceDiscGroup)).addDataSource(tableNum(RetailChannelPriceGroup));
        qbds.joinMode(JoinMode::ExistsJoin);
        qbds.relations(true);
    }
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    SysReferenceTableLookup sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(RetailChannelTable), _formReferenceControl);
    Query channelQuery;
    QueryBuildDataSource qbdsChannel;

    RetailChannelTable retailChannel;
    sysTableLookup.addLookupfield(fieldNum(RetailChannelTable, omOperatingUnitID));
    sysTableLookup.addLookupfield(fieldNum(RetailChannelTable, ChannelType));

    channelQuery = new Query();
    qbdsChannel = channelQuery.addDataSource(tableNum(RetailChannelTable));

    sysTableLookup.parmQuery(channelQuery);
    retailChannel = sysTableLookup.performFormLookup();

    return retailChannel;
}

public void init()
{
    QueryBuildDataSource qbds;

    super();

    qbds = this.query().dataSourceTable(tableNum(CatalogTranslation));
    //qbds.addLink(fieldNum(RetailCatalogPriceGroup, Catalog), fieldNum(CatalogTranslation, Catalog), tableStr(RetailCatalogPriceGroup));
    qbds.addRange(fieldNum(CatalogTranslation,Language)).value(SystemParameters::getSystemLanguageId());
}

