/// <summary>
/// Shows details form for the current reference value.
/// </summary>
public void jumpRef()
{
    args args = new Args();
    RetailChannelTable channel;

    args.caller(this);
    channel = RetailChannelTable::findByRecId(RetailChannelPriceGroup.RetailChannel);
    args.record(channel);
    args.lookupRecord(channel);
    new MenuFunction(menuitemDisplayStr(RetailStoreTable), MenuItemType::Display).run(args);
}

public void jumpRef()
{
    Args args = new Args();

    args.parmEnumType(enumNum(RetailDiscountOfferTypeBase));
    args.parmEnum(enum2int(RetailPeriodicDiscount.PeriodicDiscountType));
    args.record(RetailDiscountPriceGroup); // Add the current record for the args.

    new MenuFunction(menuitemDisplayStr(RetailDiscountsAll), MenuItemType::Display).run(args);
}
