public class RetailPriceGroupApplicabilityRule extends FormRun
{
    private Map propertyDefinitionFieldNumMap = new Map(Types::Int64, Types::Integer);
    private Map fieldNumPropertyDefinitionMap = new Map(Types::Integer, Types::Int64);

    private int channelPropValueFieldNum = 0;
    private int customerPropValueFieldNum = 0;
    private int customerGroupPropValueFieldNum = 0;
    private int affiliationPropValueFieldNum = 0;
    private int loyaltyTierPropValueFieldNum = 0;

    private container propValueFieldNums =
        [
            fieldNum(RetailPriceGroupApplicabilityRule, PropValue0),
            fieldNum(RetailPriceGroupApplicabilityRule, PropValue1),
            fieldNum(RetailPriceGroupApplicabilityRule, PropValue2),
            fieldNum(RetailPriceGroupApplicabilityRule, PropValue3),
            fieldNum(RetailPriceGroupApplicabilityRule, PropValue4),
            fieldNum(RetailPriceGroupApplicabilityRule, PropValue5),
            fieldNum(RetailPriceGroupApplicabilityRule, PropValue6),
            fieldNum(RetailPriceGroupApplicabilityRule, PropValue7),
            fieldNum(RetailPriceGroupApplicabilityRule, PropValue8),
            fieldNum(RetailPriceGroupApplicabilityRule, PropValue9)
        ];

}

public void init()
{
    RetailPricingPropertyDefinition::init(); // If no pricing properties configured, we will create and use the seed properties.

    element.initializeFieldNumMappings();
    element.buildConditionsGrid();

    super();

    boolean isFeatureEnabled = RetailPricingFeatureToggle::isRetailPricingPropertyFeatureEnabled();
    SwitchToLegacyButton.visible(!isFeatureEnabled);
    if (!isFeatureEnabled)
    {
        info("@RetailPricing:RetailPricingPropertyFeatureEnableReminderLabel");
    }

    RetailPricingSharedParameters sharedParameters = RetailPricingSharedParameters::find();
    if (sharedParameters.PriceGroupApplicabilityRuleProcessStatus == RetailPricingPropertyProcessStatus::Processing)
    {
        RetailPriceGroupApplicabilityRule_ds.allowEdit(false);    
        RetailPriceGroupApplicabilityRule_ds.allowCreate(false);    
        RetailPriceGroupApplicabilityRule_ds.allowDelete(false);    
    }

    if (!RetailPricingFeatureToggle::isRetailPricingPropertyFeatureVisible())
    {
        Args args = element.args();

        MenuFunction menuFunction = new MenuFunction(menuitemDisplayStr(RetailPriceDiscGroup), MenuItemType::Display);
        menuFunction.run(args);
    }
}

/// <summary>
/// Initializes the mapping between property RecId and fieldNum of RetailPriceGroupApplicabilityRule table.
/// </summary>
internal void initializeFieldNumMappings()
{
    RetailPricingPropertyDefinition propertyDefinition;
    EcoResAttribute ecoResAttribute;
    int propValueIndex = 1;

    while select propertyDefinition
        order by propertyDefinition.RecId asc
        where propertyDefinition.PropertyLevel == RetailPricingPropertyLevel::Header
            && propertyDefinition.Status == RetailPricingPropertyProcessStatus::Published
        outer join RecId from ecoResAttribute // Actually we don't need ecoResAttribute, but due to the builtin cache mechanism, we have to make it same as query in buildConditionsGrid.
            where ecoResAttribute.RecId == propertyDefinition.EcoResAttributeRecId
    {
        int fieldNumber = conPeek(propValueFieldNums, propValueIndex);
        RecId propertyDefinitionRecId = propertyDefinition.RecId;
        propertyDefinitionFieldNumMap.add(propertyDefinitionRecId, fieldNumber);
        fieldNumPropertyDefinitionMap.add(fieldNumber, propertyDefinitionRecId);

        if (propertyDefinition.PropertyType == RetailPricingPropertyType::Predefined)
        {
            switch(propertyDefinition.PredefinedProperty)
            {
                case RetailPricingPredefinedProperty::Channel:
                    channelPropValueFieldNum = fieldNumber;
                    break;
                case RetailPricingPredefinedProperty::Customer:
                    customerPropValueFieldNum = fieldNumber;
                    break;
                case RetailPricingPredefinedProperty::CustomerGroup:
                    customerGroupPropValueFieldNum = fieldNumber;
                    break;
                case RetailPricingPredefinedProperty::Affiliation:
                    affiliationPropValueFieldNum = fieldNumber;
                    break;
                case RetailPricingPredefinedProperty::LoyaltyTier:
                    loyaltyTierPropValueFieldNum = fieldNumber;
                    break;
                default:
                    break;
            }
        }

        propValueIndex ++;
    }
}

/// <summary>
/// Adds columns to Conditions grid according to property definition.
/// </summary>
internal void buildConditionsGrid()
{
    RetailPricingPropertyDefinition propertyDefinition;
    EcoResAttribute ecoResAttribute;

    FormBuildGridControl gridControl = element.form().design().control(formControlStr(RetailPriceGroupApplicabilityRule, ConditionsGrid));
    FormBuildDataSource dataSource = element.form().dataSource(tableStr(RetailPriceGroupApplicabilityRule));
    FormBuildControl dataFieldControl;
    str controlName;

    while select propertyDefinition
        order by propertyDefinition.DisplayOrder asc
        where propertyDefinition.PropertyLevel == RetailPricingPropertyLevel::Header // Header-level properties only.
            && propertyDefinition.Status == RetailPricingPropertyProcessStatus::Published
        outer join RecId, Name, AttributeType from ecoResAttribute
            where ecoResAttribute.RecId == propertyDefinition.EcoResAttributeRecId
    {
        int fieldNumber = propertyDefinitionFieldNumMap.lookup(propertyDefinition.RecId);
        controlName = 'Control_' + fieldId2Name(tableNum(RetailPriceGroupApplicabilityRule), fieldNumber); // Name the control in order to identify the corresponding data source easily.
        if (propertyDefinition.PropertyType == RetailPricingPropertyType::Predefined)
        {
            // For pre-defined properties, use the RefRecId fields as the underlying data source, in order to display lookup.
            switch(propertyDefinition.PredefinedProperty)
            {
                case RetailPricingPredefinedProperty::Channel:
                    fieldNumber = fieldNum(RetailPriceGroupApplicabilityRule, RetailChannelTableRefRecId);
                    dataFieldControl = gridControl.addDataFieldWithName(dataSource.id(), fieldNumber, controlName);
                    break;

                case RetailPricingPredefinedProperty::Customer:
                    fieldNumber = fieldNum(RetailPriceGroupApplicabilityRule, CustTableRefRecId);
                    dataFieldControl = gridControl.addDataFieldWithName(dataSource.id(), fieldNumber, controlName);
                    break;

                case RetailPricingPredefinedProperty::CustomerGroup:
                    fieldNumber = fieldNum(RetailPriceGroupApplicabilityRule,CustGroupRefRecId );
                    dataFieldControl = gridControl.addDataFieldWithName(dataSource.id(), fieldNumber, controlName);
                    break;

                case RetailPricingPredefinedProperty::Affiliation:
                    fieldNumber = fieldNum(RetailPriceGroupApplicabilityRule, RetailAffiliationRefRecId);
                    dataFieldControl = gridControl.addDataFieldWithName(dataSource.id(), fieldNumber, controlName);

                    var formBuildReferenceGroupControl = dataFieldControl as FormBuildReferenceGroupControl;
                    formBuildReferenceGroupControl.registerOverrideMethod(methodStr(FormReferenceGroupControl, lookup), formMethodStr(RetailPriceGroupApplicabilityRule, lookupAffiliation), this);
                    break;

                case RetailPricingPredefinedProperty::LoyaltyTier:
                    // Add a view-only field for loyalty program so that it is clear to users which program the loyalty tier belongs to.
                    fieldNumber = fieldNum(RetailPriceGroupApplicabilityRule, RetailLoyaltyProgramRefRecId);
                    dataFieldControl = gridControl.addDataFieldWithName(dataSource.id(), fieldNumber, controlName + '_LoyaltyProgram');
                    formBuildReferenceGroupControl = dataFieldControl as FormBuildReferenceGroupControl;
                    formBuildReferenceGroupControl.enabled(false);

                    fieldNumber = fieldNum(RetailPriceGroupApplicabilityRule, RetailLoyaltyTierRefRecId);
                    dataFieldControl = gridControl.addDataFieldWithName(dataSource.id(), fieldNumber, controlName);

                    formBuildReferenceGroupControl = dataFieldControl as FormBuildReferenceGroupControl;
                    formBuildReferenceGroupControl.registerOverrideMethod(methodStr(FormReferenceGroupControl, lookup), formMethodStr(RetailPriceGroupApplicabilityRule, lookupLoyaltyTier), this);
                    break;

                default:
                    break;
            }
        }
        else if (propertyDefinition.PropertyType == RetailPricingPropertyType::EcoResAttribute)
        {
            EcoResAttributeType attributeType = EcoResAttributeType::find(ecoResAttribute.AttributeType);

            dataFieldControl = gridControl.addDataFieldWithName(dataSource.id(), fieldNumber, controlName);
            FormBuildStringControl formBuildStringControl = dataFieldControl as FormBuildStringControl;
            formBuildStringControl.label(ecoResAttribute.Name);

            var lookup = new RetailPricingAttributeValueHelper(ecoResAttribute.RecId);

            switch (attributeType.DataType)
            {
                case AttributeDataType::Text:
                    // If the attribute value has a list of options, display a lookup for selection.
                    if (attributeType.IsEnumeration)
                    {
                        formBuildStringControl.lookupButton(FormLookupButton::Always);
                        formBuildStringControl.registerOverrideMethod(methodStr(FormStringControl, lookup), methodStr(RetailPricingAttributeValueHelper, lookup), lookup);
                    }
                    break;
                case AttributeDataType::TrueFalse:
                    formBuildStringControl.lookupButton(FormLookupButton::Always);
                    formBuildStringControl.registerOverrideMethod(methodStr(FormStringControl, lookup), methodStr(RetailPricingAttributeValueHelper, lookupBoolean), lookup);

                    break;
                case AttributeDataType::Integer:
                    formBuildStringControl.registerOverrideMethod(methodStr(FormStringControl, validate), methodStr(RetailPricingAttributeValueHelper, validateInt), lookup);
                    break;
            }
        }
        else
        {
            this.initExtensionPropertyControl(propertyDefinition, gridControl, dataSource);
        }
    }
}

