private void addRetailDiscountPriceGroups(FormDataSource _dataSource, PriceDiscGroup _priceDiscGroup, RetailDiscountOfferTypeBase _discountType)
{
    var parameter = RetailDiscountPriceGroupDiscountSelectionParameter::construct();
    parameter.parmPriceDiscGroup(_priceDiscGroup);
    parameter.parmPeriodicDiscountType(_discountType);

    Args args = new Args();
    args.caller(element);
    args.parmObject(parameter);

    MenuFunction menuFunction = new MenuFunction(menuitemDisplayStr(RetailDiscountPriceGroupDiscountSelection), MenuItemType::Display);
    FormRun formRun = menuFunction.create(args);
    formRun.run();
    formRun.wait();

    _dataSource.research();
}

private void removeSelectedRetailDiscountPriceGroups(FormDataSource _dataSource)
{
    RetailDiscountPriceGroup selectedRetailDiscountPriceGroup;
    DialogButton confirmDelete;

    if (_dataSource.anyMarked())
    {
        confirmDelete = Box::yesNo("@RetailPricing:RetailPriceGroupApplicabilityRuleConfirmDiscountDeleteMessage", DialogButton::No, "@SYS24848");

        if (confirmDelete)
        {
            selectedRetailDiscountPriceGroup = _dataSource.getFirst(true);

            while (selectedRetailDiscountPriceGroup)
            {
                selectedRetailDiscountPriceGroup.delete();

                selectedRetailDiscountPriceGroup = _dataSource.getNext();
            }
        }
    }

    _dataSource.research();
}

private void jumpRef(RetailDiscountOfferTypeBase _discountType, RetailDiscountPriceGroup _retailDiscountPriceGroup)
