    args.record(_retailDiscountPriceGroup);

    new MenuFunction(menuitemDisplayStr(RetailDiscountApplicabilityRule), MenuItemType::Display).run(args);
}

public void executeQuery()
{
    Query q = this.query();
    QueryBuildDataSource qbds = q.dataSourceTable(tableNum(PriceDiscGroup));

    qbds.addRange(fieldNum(PriceDiscGroup, Module)).value(queryValue(ModuleInventCustVend::Cust));
    qbds.addRange(fieldNum(PriceDiscGroup, Type)).value(queryValue(PriceGroupType::PriceGroup));

    if (element.args().record() && element.args().record().TableId == tableNum(PriceDiscGroup))
    {
        PriceDiscGroup _group = element.args().record();
        qbds.addRange(fieldNum(PriceDiscGroup, GroupId)).value(queryValue(_group.GroupId));
    }

    super();
}

