public void write()
{
    if (channelPropValueFieldNum > 0)
    {
        RetailPriceGroupApplicabilityRule.(channelPropValueFieldNum) = RetailPriceGroupApplicabilityRule.RetailChannelTableRefRecId > 0 ?
            int642Str(RetailPriceGroupApplicabilityRule.RetailChannelTableRefRecId) : '';
    }
    if (customerPropValueFieldNum > 0)
    {
        RetailPriceGroupApplicabilityRule.(customerPropValueFieldNum) = RetailPriceGroupApplicabilityRule.CustTableRefRecId > 0 ?
            CustTable::findRecId(RetailPriceGroupApplicabilityRule.CustTableRefRecId).AccountNum : '';
    }
    if (customerGroupPropValueFieldNum > 0)
    {
        RetailPriceGroupApplicabilityRule.(customerGroupPropValueFieldNum) = RetailPriceGroupApplicabilityRule.CustGroupRefRecId > 0 ?
            int642Str(RetailPriceGroupApplicabilityRule.CustGroupRefRecId) : '';
    }
    if (affiliationPropValueFieldNum > 0)
    {
        RetailPriceGroupApplicabilityRule.(affiliationPropValueFieldNum) = RetailPriceGroupApplicabilityRule.RetailAffiliationRefRecId > 0 ?
            int642Str(RetailPriceGroupApplicabilityRule.RetailAffiliationRefRecId) : '';
    }
    if (loyaltyTierPropValueFieldNum > 0)
    {
        RetailPriceGroupApplicabilityRule.(loyaltyTierPropValueFieldNum) = RetailPriceGroupApplicabilityRule.RetailLoyaltyTierRefRecId > 0 ?
            int642Str(RetailPriceGroupApplicabilityRule.RetailLoyaltyTierRefRecId) : '';
    }

    super();
}

public void modified()
{
    RetailLoyaltyTier loyaltyTier;
    select firstonly loyaltyTier where loyaltyTier.RecId == RetailPriceGroupApplicabilityRule.RetailLoyaltyTierRefRecId;

    RetailPriceGroupApplicabilityRule.RetailLoyaltyProgramRefRecId = loyaltyTier.Affiliation;

    super();
}

