public void executeQuery()
{
    QueryBuildDataSource qbds = this.query().dataSourceTable(tableNum(PriceDiscTable));
    QueryBuildRange qbr = SysQuery::findOrCreateRange(qbds, fieldNum(PriceDiscTable, AccountCode));
    qbr.value(queryValue(PriceDiscPartyCodeType::GroupId));
    qbr = SysQuery::findOrCreateRange(qbds, fieldNum(PriceDiscTable, AccountRelation));
    qbr.value(queryValue(PriceDiscGroup.GroupId));

    super();
}

