public void clicked()
{
    // We will remove the record in one of the data sources (i.e. TenderDiscountPriceGroup_ds) and leave the others untouched.
    RetailTenderDiscountPriceGroup selectedTenderDiscountPriceGroup;
    DialogButton confirmDelete;

    if (TenderDiscountPriceGroup_ds.anyMarked())
    {
        confirmDelete = Box::yesNo("@RetailPricing:RetailPriceGroupApplicabilityRuleConfirmDiscountDeleteMessage", DialogButton::No, "@SYS24848");

        if (confirmDelete)
        {
            selectedTenderDiscountPriceGroup = TenderDiscountPriceGroup_ds.getFirst(true);

            while (selectedTenderDiscountPriceGroup)
            {
                selectedTenderDiscountPriceGroup.delete();

                selectedTenderDiscountPriceGroup = TenderDiscountPriceGroup_ds.getNext();
            }
        }
    }

    TenderDiscountPriceGroup_ds.research();

    super();
}
