Method.Source:
using Microsoft.Dynamics.Commerce.Headquarters.Instrumentation;

[Form]
public class RetailProductUpdateForm extends FormRun
{
    private static CommerceHQEventSource eventSource = CommerceHQEventSource::Log;

    str textToDisplay;
    RetailCategoryProductUpdateParm inputParameters;
    RetailProductUpdateMode runMode;
    QueryBuildRange filterEntityType;
    QueryBuildRange filterEntityId;

}

public void init()
{
    if (element.args().parmObject())
    {
        inputParameters = element.args().parmObject();
        runMode = inputParameters.parmEntityType();
    }
    else
    {
        throw error("@RET6299");
    }

    super();

    this.initializeFormElements();

    RetailProductAttributesToUpdate_ds.object(fieldNum(RetailProductAttributesToUpdate, SalesRetailInventoryAvailabilityBuffer)).visible(true);
    RetailProductAttributesToUpdate_ds.object(fieldNum(RetailProductAttributesToUpdate, SalesRetailInventoryAvailabilityLevelProfile)).visible(true);
}

/// <summary>
/// Initializes the form with input data
/// </summary>
/// <exception cref="Exception::Error">
/// thrown when the update mode is not suported.
/// </exception>
private void initializeFormElements()
{
    // <GEERU>
    #ISOCountryRegionCodes
    // </GEERU>

    RefRecId productId;

    if (runMode == RetailProductUpdateMode::Category)
    {
        textToDisplay = "@RET6300" + " " + inputParameters.parmEntityName();
        categoryInfoTxt.text(textToDisplay);

        additionalCriteriaPanel.enabled(true);
        additionalCriteriaPanel.visible(true);
        scheduleButton.enabled(true);
        scheduleButton.visible(true);

        // <GBR>
        if (BrazilParameters::isEnabled())
        {
            FiscalInformation_BR.visible(true);
        }
        // </GBR>

        // <GEERU>
        if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
        {
            Return_RU.visible(true);
            labelAttributes.visible(true);
        }
        // </GEERU>
    }
    else if ((runMode == RetailProductUpdateMode::Product) || (runMode == RetailProductUpdateMode::ProductSet))
    {
        textToDisplay = "@RET6300" + " " + inputParameters.parmEntityName();
        categoryInfoTxt.text(textToDisplay);

        additionalCriteriaPanel.enabled(false);
        additionalCriteriaPanel.visible(false);
        scheduleButton.enabled(false);
        scheduleButton.visible(false);

        // <GBR>
        if (BrazilParameters::isEnabled())
        {
            FiscalInformation_BR.visible(true);
        }
        // </GBR>

        // <GEERU>
        if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
        {
            Return_RU.visible(true);
            labelAttributes.visible(true);
        }
        // </GEERU>

        // Hide info code group for non-released product
        productId = inputParameters.parmEntityId();
        if (!InventTable::findByProduct(productId).RecId)
        {
            Infocodes.visible(false);
        }
    }
    else
    {
        eventSource.EventWriteProductsGenericError('Category hierarchy', funcName(), 'Attempt to call product update form with unknown mode.');
        throw error("@RET6301");
    }
}

public void initValue()
{
    RetailProductAttributesToUpdate options;

    options = RetailProductAttributesToUpdate::findByEntityTypeAndId(
        inputParameters.parmEntityType(),
        inputParameters.parmEntityId(),
        true);

    super();

    if (!options.RecId)
    {
        ttsbegin;
            //options.selectForUpdate(true);
            options.EntityType = inputParameters.parmEntityType();
            options.EntityId = inputParameters.parmEntityId();
            options.insert();
        ttscommit;
    }

    // this sets the fields in the datasource record for RetailProductUpdateOptions
    retailProductAttributesToUpdate.data(options);
}

public void init()
{
    QueryBuildDataSource    queryBuildDataSource;
    ;

    super();

    queryBuildDataSource = this.query().dataSourceTable(tableNum(RetailProductAttributesToUpdate));
    filterEntityType = queryBuildDataSource.addRange(fieldNum(RetailProductAttributesToUpdate, EntityType));
    filterEntityId = queryBuildDataSource.addRange(fieldNum(RetailProductAttributesToUpdate, EntityId));
}

void clicked()
{
    RetailProductUpdater updater;
    FormRun              caller;
    RetailICategory      retailCategoryForm;

    super();

    updater = new RetailProductUpdater();
    updater.parmProductUpdateParameters(inputParameters);

    if (runMode == RetailProductUpdateMode::Category)
    {
        updater.parmIncludeSubCategories(includeSubCategories.checked());
        updater.parmOverrideProductProperties(includeCustomizedProducts.checked());
        updater.parmShowProgress(true);
        updater.updateProductsInCategory();
        caller = element.args().caller();

        // clean up the categories old values  as the products have been updated.
        if (caller
            && classIdGet(caller) == classNum(FormRun)
            && formHasMethod(caller, identifierStr(cleanCategoryBackupProperties))
            && formHasMethod(caller, identifierStr(refreshStaticAttributes)))
        {
            retailCategoryForm = caller as RetailICategory;
            retailCategoryForm.cleanCategoryBackupProperties(inputParameters.parmEntityId());
            retailCategoryForm.refreshStaticAttributes();
        }
    }
    else if (runMode == RetailProductUpdateMode::Product)
    {
        updater.parmIncludeSubCategories(false);
        updater.parmOverrideProductProperties(true);
        updater.parmShowProgress(false);
        updater.updateSingleProductFromCategory();
    }
    else if (runMode == RetailProductUpdateMode::ProductSet)
    {
        updater.parmIncludeSubCategories(false);
        updater.parmOverrideProductProperties(true);
        updater.parmShowProgress(false);
        updater.updateMultipleProductsFromCategory();
    }
    else
    {
        throw error("@RET6301");
    }
}

void clicked()
{
    BatchHeader batchHeader;
    BatchInfo batchInfo;
    RetailProductUpdater updater;
    str jobCaption = strFmt("@RET260001", inputParameters.parmEntityName());

    super();

    updater = RetailProductUpdater::construct();

    updater.parmProductUpdateParameters(inputParameters);
    updater.parmIncludeSubCategories(includeSubCategories.checked());
    updater.parmOverrideProductProperties(includeCustomizedProducts.checked());
    updater.parmShowProgress(false);

    batchInfo = updater.batchInfo();
    batchInfo.parmCaption(jobCaption);
    batchInfo.parmGroupId(''); // The "Empty batch group".
    batchHeader = BatchHeader::construct();
    batchHeader.addTask(updater);
    batchHeader.save();

    eventSource.EventWriteProductsRetailProductUpdateFormJobScheduled('Category hierarchy', funcName());
    info(strFmt("@RET260002", jobCaption));
}
