[Form]
public class RetailRecoListConfigurations extends FormRun
{
    QueryBuildRange filterNewListTypeRange;
    QueryBuildRange filterTrendingListTypeRange;
    QueryBuildRange filterBestsellingListTypeRange;
    QueryBuildRange filterCartListTypeRange;
    QueryBuildRange filterPeopleAlsoBuyListTypeRange;

}

    public void init()
    {
        super();

        RetailSharedParameters sharedParms = RetailSharedParameters::find();
		if (!sharedParms.RecoListEnabled)
        {
            warning("@Retail:RetailSharedParametersRecoListNotEnabledWarning", 'https://go.microsoft.com/fwlink/?linkid=2124032');
        }

        str linkText = strFmt("<a target='_blank' href='%2'>%1</a>", "@Retail:RecoListDescriptionIncludeExcludeFeatureDocumentationLink", 'https://go.microsoft.com/fwlink/?linkid=2100618');
        str bodyText = strFmt("@Retail:RecoListDescriptionIncludeExcludeProducts", linkText);

        RecoListNewAddRemoveProductsDescriptionHtmlView.parmHtml(strFmt("<html><body>%1</body></html>", bodyText));
        RecoListTrendingAddRemoveProductsDescriptionHtmlView.parmHtml(strFmt("<html><body>%1</body></html>", bodyText));
        RecoListBestsellingAddRemoveProductsDescriptionHtmlView.parmHtml(strFmt("<html><body>%1</body></html>", bodyText));
        RecoListCartAddRemoveProductsDescriptionHtmlView.parmHtml(strFmt("<html><body>%1</body></html>", bodyText));
        RecoListPeopleAlsoBuyAddRemoveProductsDescriptionHtmlView.parmHtml(strFmt("<html><body>%1</body></html>", bodyText));
    }

        public void init()
        {
            super();
            filterNewListTypeRange = this.query().dataSourceTable(tablenum(RetailRecoListConfigurationParameters)).addRange(fieldnum(RetailRecoListConfigurationParameters, ListType));
            this.queryBuildDataSource().addSortField(fieldNum(RetailRecoListConfigurationParameters, DisplayOrder), SortOrder::Ascending);
		}

        public void write()
        {
            RetailRecoListConfigurationParameters_New.ListType = RetailRecoListTypeBase::New;
            if (RetailRecoListConfigurationParameters_New.LineType == RetailRecoListExcludeIncludeType::Exclude)
            {
				// Lines that are excluded do not get display order set.
                RetailRecoListConfigurationParameters_New.DisplayOrder = 0;
            }

            super();
            this.reread();
            this.refreshEx();
        }

        public void init()
        {
            super();
            filterTrendingListTypeRange = this.query().dataSourceTable(tablenum(RetailRecoListConfigurationParameters)).addRange(fieldnum(RetailRecoListConfigurationParameters, ListType));
            this.queryBuildDataSource().addSortField(fieldNum(RetailRecoListConfigurationParameters, DisplayOrder), SortOrder::Ascending);
		}

public void write()
{
