/// <summary>
/// Initializes the form.
/// </summary>
public void init()
{
    super();

    SalesLineGroup.visible(true); // show order lines grid by default
}

/// <summary>
/// Initializes data source with rejected lines only.
/// </summary>
public void init()
{
    super();
    this.query().dataSourceTable(tableNum(RetailSalesLine)).addRange(fieldNum(RetailSalesLine, FulfillmentStatus)).value(queryValue(RetailFulfillmentLineStatus::Rejected));
}

/// <summary>
/// Initializes data source with rejected lines only.
/// </summary>
public void init()
{
    super();

    this.query().dataSourceTable(tableNum(RetailSalesLine)).addRange(fieldNum(RetailSalesLine, FulfillmentStatus)).value(queryValue(RetailFulfillmentLineStatus::Rejected));
}

/// <summary>
/// Toggle grid for rejected lines of selected order.
/// </summary>
public void clicked()
{
    super();

    SalesLineGroup.visible(!SalesLineGroup.isVisible());
}
