[Form]
public class RetailReleasedProductsPart extends FormRun implements SysIFilterEventHandler
{
    const str KitFilterOption_IncludeKits = "@Retail:ProductFilterOptionIncludeKits";
    const str KitFilterOption_ExcludeKits = "@Retail:ProductFilterOptionExcludeKits";
    const str KitFilterOption_KitsOnly = "@Retail:ProductFilterOptionKitsOnly";

    const str DetailsOption_ProductVariants = "@Retail:ProdDetailsOptionProductVariants";
    const str DetailsOption_PublishedAssortments = "@Retail:ProdDetailsOptionAssortments";
    const str DetailsOption_RelatedProducts = "@Retail:ProdDetailsOptionRelatedProducts";

}

public void init()
{
    super();

    ToggleProductDetailsSection.OnClicked += eventhandler(this.toggleDetailSectionVisibility);

    KitFilterOption.add(KitFilterOption_IncludeKits);
    KitFilterOption.add(KitFilterOption_ExcludeKits);
    KitFilterOption.add(KitFilterOption_KitsOnly);

    DetailsSectionSelector.add(DetailsOption_ProductVariants);
    DetailsSectionSelector.add(DetailsOption_PublishedAssortments);
    DetailsSectionSelector.add(DetailsOption_RelatedProducts);

    // Set product filter option to show all products including kits.
    this.setProductGridFilterOption(KitFilterOption.valueStr());

    // Set details section to show product variants.
    this.selectDetailType(DetailsOption_ProductVariants);

    this.onFilterChanged();

    this.setupFallbackProductTranslation();
}

private void setupFallbackProductTranslation()
{
    List fallbackStringControls = new List(Types::Integer);
    List ecoResProductTranslationStringControls = new List(Types::Integer);
    EcoResProductTranslationFallbackViewModel fallbackViewModel;

    ecoResProductTranslationStringControls.addEnd(element.controlId(formControlStr(RetailReleasedProductsPart, EcoResProductTranslation_Name)));
    fallbackStringControls.addEnd(element.controlId(formControlStr(RetailReleasedProductsPart, EcoResProductTranslationFallbackView_Name)));

    EcoResProductTranslationFallbackViewModel::setupFallbackTranslationForm(element,
                                                                            element.EcoResProductTranslationFallbackView_ds,
                                                                            element.EcoResProduct_ds,
                                                                            fallbackStringControls,
                                                                            ecoResProductTranslationStringControls);

    // Related products
    fallbackStringControls = new List(Types::Integer);
    ecoResProductTranslationStringControls = new List(Types::Integer);

    ecoResProductTranslationStringControls.addEnd(element.controlId(formControlStr(RetailReleasedProductsPart, EcoResProductTranslation_RelatedProducts_Name)));
    fallbackStringControls.addEnd(element.controlId(formControlStr(RetailReleasedProductsPart, RelatedProducts_EcoResProductTranslationFallbackView_Name)));

    EcoResProductTranslationFallbackViewModel::setupFallbackTranslationForm(element,
                                                                            element.RelatedProducts_EcoResProductTranslationFallbackView_ds,
                                                                            element.RelatedProducts_EcoResProductRelationTable_ds,
                                                                            fallbackStringControls,
                                                                            ecoResProductTranslationStringControls);
}

/// <summary>
/// Event handler to react on a filter change.
/// </summary>
public void onFilterChanged()
{
    this.applyCategoryFilter();
    this.applyOrgHierarchyFilter();

    InventTable_DS.executeQuery();
}

/// <summary>
/// Retrieves the workspace level org hierachy filter from <c>RetailCategoryAndProductWorkspace</c>
/// and applies it to the current form <c>RetailReleasedProductsPart</c>.
/// </summary>
private void applyOrgHierarchyFilter()
{
    // Retrieve the org hierarchy filter from the parent form
    SysIFilter  filter = this.parmFilterProvider().parmFilter();
    RecId omHierarchyRelationshipRecId = SysFilterValueHelper::getFirstValueRecordFromFilterForType(filter, SysFilterValueTypeEnum::Integer, extendedTypeNum(RecId));

    // If org hierarchy filter is blank disable ChannelFilter_EcoResProduct datasource so as to show all products
    // including those which are not assiciated(assorted) to any channel.
    if (omHierarchyRelationshipRecId == 0)
    {
        ChannelFilter_OMHierarchyRelationship_DS.queryBuildDataSource().clearRanges();
        ChannelFilter_EcoResProduct_DS.queryBuildDataSource().enabled(false);
    }
    else
    {
        ChannelFilter_EcoResProduct_DS.queryBuildDataSource().enabled(true);

        // Filter only the products which are assorted to the channels specified by the current org hierarchy filter.
        QueryBuildRange omHierarchyRelationShipRange = ChannelFilter_OMHierarchyRelationship_DS.queryBuildDataSource().addRange(fieldNum(OMHierarchyRelationship, RecId));
        omHierarchyRelationShipRange.value(queryValue(omHierarchyRelationshipRecId));
    }
}

/// <summary>
/// Retrieves the workspace level category filter from <c>RetailCategoryAndProductWorkspace</c>
/// and applies it to the current form <c>RetailReleasedProductsPart</c>.
/// </summary>
private void applyCategoryFilter()
{
    // Retrieve the workspace level category filter from the RetailCategoryAndProductWorkspace.
    SysIFilter  filter = this.parmFilterProvider().parmFilter();

    EcoResCategoryHierarchyId categoryHierarchyId = SysFilterValueHelper::getFirstValueRecordFromFilterForType(filter, SysFilterValueTypeEnum::Integer, extendedTypeNum(EcoResCategoryHierarchyId));

    // If categoy filter is blank disable EcoResProductCategory datasource to show all products including those which do not belong in any category.
    if (categoryHierarchyId == 0)
    {
        CategoryFilter_EcoResProductCategory_DS.queryBuildDataSource().enabled(false);
    }
    else
    {   // If category filter is not blank then enable the join on EcoResProductCategory datasource and
        // apply the category filter on EcoResCategory datasource ranges.

        CategoryFilter_EcoResProductCategory_DS.queryBuildDataSource().enabled(true);

        // apply category filter.
        QueryBuildDataSource EcoResCategoryQBDS = CategoryFilter_EcoResCategory_DS.queryBuildDataSource();
        EcoResCategoryNestedSetRight categoryNestedSetRight = SysFilterValueHelper::getFirstValueRecordFromFilterForType(filter, SysFilterValueTypeEnum::Integer, extendedTypeNum(EcoResCategoryNestedSetRight));
        EcoResCategoryNestedSetLeft categoryNestedSetLeft = SysFilterValueHelper::getFirstValueRecordFromFilterForType(filter, SysFilterValueTypeEnum::Integer, extendedTypeNum(EcoResCategoryNestedSetLeft));

        QueryBuildRange categoryHierarchyRange = EcoResCategoryQBDS.addRange(fieldNum(EcoResCategory, CategoryHierarchy));
        categoryHierarchyRange.value(queryValue(categoryHierarchyId));

        QueryBuildRange nestedSetLeftRange = EcoResCategoryQBDS.addRange(fieldNum(EcoResCategory, NestedSetLeft));
        nestedSetLeftRange.value(Global::queryRange(categoryNestedSetLeft, categoryNestedSetRight));

        QueryBuildRange nestedSetRightRange = EcoResCategoryQBDS.addRange(fieldNum(EcoResCategory, NestedSetRight));
        nestedSetRightRange.value(Global::queryRange(categoryNestedSetLeft, categoryNestedSetRight));

        QueryBuildRange isActiveRange = EcoResCategoryQBDS.addRange(fieldNum(EcoResCategory, IsActive));
        isActiveRange.value(queryValue(NoYes::Yes));
    }
}

private void setProductGridFilterOption(str productFilterOption)
{
    switch (productFilterOption)
    {
        case KitFilterOption_IncludeKits:
            // Disables the exist join on the RetailKit datasource to show all products including kits.
            KitFilter_RetailKit_DS.queryBuildDataSource().enabled(false);
            break;
        case KitFilterOption_ExcludeKits:
            // Enable the Retailkit datasource and set the join mode to filter out all kit products.
            KitFilter_RetailKit_DS.queryBuildDataSource().enabled(true);
            KitFilter_RetailKit_DS.queryBuildDataSource().joinMode(JoinMode::NoExistsJoin);
            break;
        case KitFilterOption_KitsOnly:
            // Enable the join on RetailKit datasource and set the join mode to filter out all non-kit products.
            KitFilter_RetailKit_DS.queryBuildDataSource().enabled(true);
            KitFilter_RetailKit_DS.queryBuildDataSource().joinMode(JoinMode::ExistsJoin);
            break;
    }

    InventTable_DS.executeQuery();
}

private void selectDetailType(str detailType)
{
    ProductVariants.visible(DetailsSectionSelector.valueStr() == DetailsOption_ProductVariants);

    Assortments.visible(DetailsSectionSelector.valueStr() == DetailsOption_PublishedAssortments);

    RelatedProducts.visible(DetailsSectionSelector.valueStr() == DetailsOption_RelatedProducts);
}

private void toggleDetailSectionVisibility(FormControl _sender, FormControlEventArgs _e)
{
    FormButtonControl switchButton = _sender as FormButtonControl;

    ProductDetailGroup.visible(!ProductDetailGroup.visible());

    if (ProductDetailGroup.visible())
    {
        switchButton.normalImage(ImageReferenceSymbol::CaretLeft);
        switchButton.text("@Retail:HideDetails");
    }
    else
    {
        switchButton.normalImage(ImageReferenceSymbol::CaretRight);
        switchButton.text("@Retail:ShowDetails");
    }
}

public void selectionChanged()
{
    super();
    RetailAssortmentTable_DS.executeQuery();
}

public void init()
{
    super();
    EcoResProductTranslation::queryAddDefaultLanguage(this.query());
}

public void executeQuery()
{
    QueryBuildDataSource qbds;
    qbds = RetailAssortmentLookup_DS.query().dataSourceTable(tableNum(RetailAssortmentLookup));
    qbds.clearRanges();

    QueryBuildRange qbr;
    qbr = qbds.addRange(fieldNum(RetailAssortmentLookup, ProductId));

    qbr.value(SysQuery::value(EcoResProduct.RecId ? EcoResProduct.RecId : 0));

    super();
}

public void init()
{
    super();

    EcoResProductTranslation::queryAddDefaultLanguage(this.query());

    QueryBuildDataSource qbdsProductTranslation = this.queryBuildDataSource();

    qbdsProductTranslation.addRange(fieldNum(EcoResProductTranslation, LanguageId)).value(queryValue(SystemParameters::getSystemLanguageId()));
    qbdsProductTranslation.addLink(fieldNum(EcoResProductRelationTable, Product2), fieldNum(EcoResProductTranslation, Product));
}

public void init()
{
    if (!isConfigurationkeyEnabled(configurationKeyNum(EcoResProductSize)))
    {
        this.linkType(FormLinkType::Passive);
        this.joinSource('');
    }

    super();

    if (isConfigurationkeyEnabled(configurationKeyNum(EcoResProductSize)))
    {
        this.queryBuildDataSource().addLink(fieldNum(EcoResDistinctProductVariant, RecId), fieldNum(EcoResProductVariantSizeDisplayOrderView, DistinctProductVariant));

        this.queryBuildDataSource().addOrderByField(fieldNum(EcoResProductVariantSizeDisplayOrderView, RetailDisplayOrder));
        this.queryBuildDataSource().addOrderByField(fieldNum(EcoResProductVariantSizeDisplayOrderView, Name));
    }
}

