Method.Source:
/// <summary>
/// The channel return policy configuration form.
/// </summary>
[Form]
public class RetailReturnPolicyChannel extends FormRun implements RetailIOperatingUnitPickerCaller
{
    RetailChannelType channelType = RetailChannelType::RetailStore;
    private const int RetryNum = 2;

}

/// <summary>
/// Gets the initial selection for the operating unit picker.
/// </summary>
/// <returns>The operating unit hierarchy element to initially select.</returns>
public RetailOperatingUnitPickerData getPickerInitParameters()
{
    // No need to pass already selected orgs so we just pass the hierarchy purpose as an init parameter for the picker.
    return RetailOperatingUnitPickerData::createInstance(HierarchyPurpose::RetailAssortment);
}

/// <summary>
/// Callback of RetailOperatingUnitPicker.
/// After organization units are selected, this method will be called to
/// 1. Validate the selected organization units
/// 2. Update selected organization units to RetailChannelTable.
/// </summary>
/// <param name = "ouPickerData">
/// The set of selected organization units.
/// </param>
public void processOperatingUnitPickerData(RetailOperatingUnitPickerData ouPickerData)
{
    if (RetailReturnPolicyChannelHelper::processOUPickerDataAndUpdateChannelTableWithPolicy(ouPickerData, retailReturnPolicyChannel))
    {
        RetailChannelTable_ds.research(true);
    }
}

/// <summary>
/// If there is no active return policy, do not allow adding/removing refund payment methods; otherwise allow it.
/// </summary>
internal void updateMappingEntryControlsStatus()
{
    boolean isEnabled = retailReturnPolicyChannel.RecId != 0;

    AllowedRefundPaymentMethods.enabled(isEnabled);
}

public void initValue()
{
    super();

    // If opened from RetailStoreTable, the channel policies are pre filtered. When creating new policy, needs to clear out the query build range.
    retailReturnPolicyChannel.PolicyName = "";
}

/// <summary>
/// Update when user's selection change of return policy.
/// </summary>
public void selectionChanged()
{
    super();

    if (element.args().dataset() == tableNum(RetailChannelTable))
    {
        return;
    }

    RetailReturnPolicyChannelRecId policyRecId = retailReturnPolicyChannel.PolicyName != "" ? retailReturnPolicyChannel.RecId : -1;

    element.updateMappingEntryControlsStatus();

    RetailTenderTypeTable_ds.loadPaymentMappings();

    QueryBuildDataSource ds = RetailChannelTable_ds.query().dataSourceTable(tableNum(RetailChannelTable));
    ds.clearRanges();
    ds.addRange(fieldnum(RetailChannelTable, RetailReturnPolicyChannel)).value(queryValue(policyRecId));
    ds.addRange(fieldnum(RetailChannelTable, ChannelType)).value(queryValue(channelType));
    RetailChannelTable_ds.executeQuery();
    retailTenderTypeTable_ds.first();
}

/// <summary>
/// Delete a policy, and related RetailReturnPolicyChannelPaymentMapping records;
/// The coresponding RetailChannelTable records will also be updated.
/// </summary>
public void delete()
{
        super();
        RetailChannelTable_ds.research(true);

}

/// <summary>
/// Validates whether associated channel records exits for the return policy.
/// </summary>
/// <returns>Return true whether channel records does not exists, otherwise false.</returns>
public boolean validateDelete()
{
    boolean ret;

    select firstonly retailChannelTable
        where retailChannelTable.RetailReturnPolicyChannel == RetailReturnPolicyChannel.RecId;

    if (retailChannelTable.RecId)
    {
        warning(strFmt("@Retail:RetailReturnPolicyDeleteValidation", RetailReturnPolicyChannel.PolicyName));
    }

    ret = super();

    return ret;
}

public void init()
{
    super();

    // Sort by line number
    retailReturnPolicyChannelPaymentMapping_ds.query().dataSourceName('RetailReturnPolicyChannelPaymentMapping').sortClear();
    retailReturnPolicyChannelPaymentMapping_ds.query().dataSourceName('RetailReturnPolicyChannelPaymentMapping').addSortField(fieldNum(RetailReturnPolicyChannelPaymentMapping, LineNumber), SortOrder::Ascending);
}

/// <summary>
/// Tender type range of the mapping table needs to be cleared and added again on user selection change of (original) tender type.
/// </summary>
public void selectionChanged()
{
    super();

    this.loadPaymentMappings();
}

public void loadPaymentMappings()
{
    if (element.args().dataset() == tableNum(RetailReturnPolicyChannelPaymentMapping))
    {
        return;
    }

    // Refresh refund method mapping grid on tender type selection change
    QueryBuildDataSource ds = RetailReturnPolicyChannelPaymentMapping_ds.query().dataSourceTable(tableNum(RetailReturnPolicyChannelPaymentMapping));
    ds.clearRanges();
    ds.addRange(fieldnum(RetailReturnPolicyChannelPaymentMapping, RetailReturnPolicyChannel)).value(queryValue(retailReturnPolicyChannel.RecId));
    ds.addRange(fieldnum(RetailReturnPolicyChannelPaymentMapping, OriginalTenderTypeId)).value(queryValue(retailTenderTypeTable.TenderTypeId));
    RetailReturnPolicyChannelPaymentMapping_ds.executeQuery();
    RetailReturnPolicyChannelPaymentMapping_ds.first();
}

public void init()
{
    super();

    // Add filter to restrict channel tpye.
    QueryBuildDataSource ds = this.query().dataSourceTable(tableNum(RetailChannelTable));
    ds.clearRanges();
    ds.addRange(fieldnum(RetailChannelTable, ChannelType)).value(queryValue(channelType));
    this.executeQuery();
}

/// <summary>
/// Clean up incomplete records when leaving the group.
/// </summary>
/// <returns></returns>
public boolean leave()
{
    boolean ret;

    ret = super();

    return ret;
}

public void clicked()
{
    super();

    if (retailReturnPolicyChannel.RecId)
    {
        RetailReturnPolicyChannelPaymentMapping mappingLine;

        mappingLine.clear();
        mappingLine.RetailReturnPolicyChannel = retailReturnPolicyChannel.RecId;
        mappingLine.OriginalTenderTypeId = retailTenderTypeTable.tenderTypeId;
        mappingLine.insert();

        RetailTenderTypeTable_ds.loadPaymentMappings();

        RetailReturnPolicyChannelPaymentMapping_ds.research();
        RetailReturnPolicyChannelPaymentMapping_ds.last();
    }
}

/// <summary>
/// This method is called when adding channel to the policy.
/// retailReturnPolicyChannel must exist in before adding channel.
/// </summary>
public void clicked()
{
    if (!retailReturnPolicyChannel.PolicyName)
    {
        error("@RetailReturnPolicy:RetailReturnPolicyChannelNoPolicyError");

        return;
    }

    super();
}

/// <summary>
/// This method is called when removing channel from the policy.
/// retailReturnPolicyChannel must exist in before adding channel.
/// </summary>
public void clicked()
{
    super();

    if (!retailReturnPolicyChannel.PolicyName)
    {
        return;
    }

    ttsbegin;

    RetailChannelTable recordsToDelete;
    recordsToDelete = RetailChannelTable_ds.getFirst(1);
    while (recordsToDelete)
    {
        recordsToDelete.selectForUpdate();
        recordsToDelete.RetailReturnPolicyChannel = 0;
        recordsToDelete.Update();

        recordsToDelete = RetailChannelTable_ds.getNext();
    }

    ttscommit;

    RetailChannelTable_ds.research();
}
