public void init()
{
    int n;
    int i;
    super();

    PolicyHtmlViewer.parmHtml(strFmt(
        "<html><body>%1&nbsp;<a href='https://go.microsoft.com/fwlink/?linkid=2038552&clcid=0x409'>%2</a></body></html>",
        "@Retail:PaymentConnectorPolicyText",
        "@Retail:PaymentConnectorPolicyLinkText"));

    customerAddressBookCtrl = SysLookupMultiSelectCtrl::construct(element, CustomerAddressBook, queryStr(DirAddressBookLookup));
    employeeAddressBookCtrl = SysLookupMultiSelectCtrl::construct(element, EmployeeAddressBook, queryStr(DirAddressBookLookup), false);

    try
    {
        connectorSetup = CreditCardConnectorSetup::construct();
        connectorSetup.setParentControl(PaymentConnectorDetail, literalStr(propertyFieldModified));
        n = connectorSetup.getCount();

        // Add all connector names to the combobox
        for (i = 1; i <= n; i++)
        {
            PaymentConnectorName.add(connectorSetup.getName(i));
        }

        // Default to the first connector if there is one
        if (n > 0)
        {
            PaymentConnectorName.selection(0);
        }
    }
    catch (Exception::CLRError)
    {
        System.Exception ex = CLRInterop::getLastException();
        ex = ex.get_InnerException();
        if (Debug::debugMode())
        {
            error(ex.ToString());
        }
        RetailTracer::Error('RetailSPOnlineStoreDetailPage', funcName(), ex.ToString());

        System.Reflection.ReflectionTypeLoadException e = ex as System.Reflection.ReflectionTypeLoadException;
        if (e != null)
        {
            System.Exception[] les = e.LoaderExceptions;
            for (int k = 0; k < les.get_length(); k++)
            {
                System.Exception le = les.get_item(k);
                if (Debug::debugMode())
                {
                    error(le.ToString());
                }
                RetailTracer::Error('RetailSPOnlineStoreDetailPage', funcName(), le.ToString());
            }
        }
    }

    PaymentConnectorDetail.visible(false);

    RetailOnlineChannelTable_ds.object(fieldNum(RetailOnlineChannelTable, CalcExemptTaxesForPriceInclusive)).visible(true);

    this.setPriceGroupsButtonVisibility();
}

