public int task(int _taskId)
{
    #Task

    int ret;

    if ((_taskId == #TaskEditRecord || _taskId ==#taskRightClickEditRecord) && this.inViewMode())
    {
        if (!this.validateAllowEdit())
        {
            return 0;
        }
    }

    ret = super(_taskId);

    return ret;
}

public void updateAddressBooks()
{
    // Get custAddressBook for backward compatibility.
    custAddressBook = this.getSelectedAddressBookNames(customerAddressBookCtrl);
    element.updateAddressBooksByType(RetailAddressBookType::Customer);
}

public void updateAddressBooksByType(RetailAddressBookType addressBookType)
{
    // Only do this if we have a retailOnlineChannelTable and its address books were loaded
    if (retailOnlineChannelTable.RecId && retailOnlineChannelTable.RecId == addressBookControlsChannelRecId)
    {
        // Gettting the address from the addressbook container
        container addressBookContainer = this.getSelectedAddressBookNames(addressBookType == RetailAddressBookType::Customer ? customerAddressBookCtrl : employeeAddressBookCtrl);
        Set selectedAddresses = new Set(Types::String);
        for (int i = 1; i <= conLen(addressBookContainer); i++)
        {
            selectedAddresses.add(conPeek(addressBookContainer, i));
        }

        // Update the addressbooks to the database
        RetailStoreAddressBook::updateAddressBooks(retailOnlineChannelTable.RecId, selectedAddresses, addressBookType);
    }
}

public void updateUI()
{
    RetailOnlineChannelPublishStatus onlineChannelPublishStatus = RetailOnlineChannelPublishStatus::findByChannel(retailOnlineChannelTable.RecId);

    boolean isDraftOrInProgress = (onlineChannelPublishStatus.PublishStatus == RetailOnlineChannelPublishStatusType::InProgress ||
                                onlineChannelPublishStatus.PublishStatus == RetailOnlineChannelPublishStatusType::Draft);

    MapAttributesButton.enabled(RetailChannelTable::findCategoryHierarchyByChannel(RetailOnlineChannelTable.RecId) > 0);
    PublishButton.enabled(isDraftOrInProgress);

    AddConnector.enabled(isDraftOrInProgress);
    RemoveConnector.enabled(isDraftOrInProgress);
    AddLine.enabled(isDraftOrInProgress);
    RemoveLine.enabled(isDraftOrInProgress);
}

/// <summary>
/// Check whether it is OK to edit the channel
/// </summary>
/// <returns>
/// A boolean result indicating whether the channel is editable
/// </returns>
public boolean validateAllowEdit()
{
    boolean ret = true;

    RetailOnlineChannelPublishStatus updateOnlineChannelPublishStatus = RetailOnlineChannelPublishStatus::findByChannel(RetailOnlineChannelTable.RecId, true);

    if (updateOnlineChannelPublishStatus.RecId && updateOnlineChannelPublishStatus.PublishStatus != RetailOnlineChannelPublishStatusType::InProgress)
    {
        ttsBegin;
        updateOnlineChannelPublishStatus.PublishStatus = RetailOnlineChannelPublishStatusType::InProgress;
        updateOnlineChannelPublishStatus.PublishStatusMessage = '';
        updateOnlineChannelPublishStatus.update();
        ttsCommit;
    }

    RetailOnlineChannelPublishStatus_ds.executeQuery();
    this.updateUI();

    return ret;
}

