public int active()
{
    #Task
    int ret;

    ret = super();

    if (!isWriting)
    {
        customerAddressBookCtrl.set(retailStoreAddressBook.addressBooks(RetailOnlineChannelTable.RecId, RetailAddressBookType::Customer));
        employeeAddressBookCtrl.set(retailStoreAddressBook.addressBooks(RetailOnlineChannelTable.RecId, RetailAddressBookType::Employee));
        addressBookControlsChannelRecId = RetailOnlineChannelTable.RecId;
    }

    // Check whether the channel can be edited
    if (!element.inViewMode() && !element.validateAllowEdit())
    {
        // Set the record to the view mode
        element.task(#TaskEditRecord);
    }

    // <GIN>
    element.enableIndiaControls_IN(retailOnlineChannelTable.isIndiaCompanyRetailStore_IN());
    // </GIN>

    DimensionEntryControl.parmCompany(RetailOnlineChannelTable.company());
    DimensionEntryControl.reactivate();

    // Hide payment connector details when the channel is opening.
    // The detail will be set to visible if the channel contains a payment connector.
    PaymentConnectorDetail.visible(false);

    RetailChannelIdFormControlHelper::setRetailChannelIdControlEditableStatus(RetailOnlineChannelTable_RetailChannelId, RetailOnlineChannelTable.RecId == 0);
    previousInventLocation = RetailOnlineChannelTable.inventLocation;

    return ret;
}

