public void write()
{
    boolean isInsert = RetailOnlineChannelTable.RecId == 0;
    isWriting = true;
    ttsbegin;

    // Ensure that RetailOnlineChannelTable and OMOperatingUnit are updated transactionally.
    oMOperatingUnit.write();

    RetailOnlineChannelTable.OMOperatingUnitID = OMOperatingUnit.RecId;
    super();

    element.numberSeqRetailChannelId().formMethodDataSourceWrite();

    if (retailOnlineChannelLanguages.RecId == 0)
    {
        retailOnlineChannelLanguages.LanguageId     = SystemParameters::systemLanguageId();
        retailOnlineChannelLanguages.OnlineChannel  = RetailOnlineChannelTable.RecId;
        retailOnlineChannelLanguages.IsDefault      = NoYes::Yes;
        retailOnlineChannelLanguages.insert();
        retailOnlineChannelLanguages_ds.reread();
    }

    if (isInsert && addressBookControlsChannelRecId == 0)
    {
        addressBookControlsChannelRecId = RetailOnlineChannelTable.RecId;

        // update the addressbooks when saving the data
        // #Bug 438872 to consolidate both insert/update places into one. Now this place is only handled for insert.
        element.updateAddressBooksByType(RetailAddressBookType::Customer);
        element.updateAddressBooksByType(RetailAddressBookType::Employee);
    }

    if (!RetailChannelTableExt.Channel)
    {
        RetailChannelTableExt.Channel = RetailOnlineChannelTable.RecId;
        RetailChannelTableExt.write();
    }
    // <GIN>
    if (isIndia)
    {
        // need to force the write in the create case since the form won't do it.
        retailChannelTable_IN.RetailChannelTable = RetailOnlineChannelTable.RecId;
        retailChannelTable_IN.TaxGroupDataAreaId = RetailOnlineChannelTable.inventLocationDataAreaId;
        retailChannelTable_IN.write();
    }
    // </GIN>

    if (RetailOnlineChannelTable.inventLocation != previousInventLocation)
    {
        Box::info("@Retail:OnlineStoreWarehouseChangeWarning");
        eventSource.EventWriteOnlineStoreWarehouseModified(RetailOnlineChannelTable.RecId, previousInventLocation, RetailOnlineChannelTable.inventLocation);
    }

    ttscommit;
    isWriting = false;
}

