public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    SysReferenceTableLookup sysTableLookup;
    Query query;
    QueryBuildDataSource hierarchDS, hierarchyRoleDS;

    sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(EcoResCategoryHierarchy), _formReferenceControl, true);
    sysTableLookup.addLookupfield(fieldNum(EcoResCategoryHierarchy, Name));

    query = new Query();
    hierarchDS = query.addDataSource(tableNum(EcoResCategoryHierarchy));
    hierarchyRoleDS = hierarchDS.addDataSource(TableNum(EcoResCategoryHierarchyRole));
    hierarchyRoleDS.relations(true);
    hierarchyRoleDS.joinMode(JoinMode::ExistsJoin);

    hierarchyRoleDS.addRange(fieldNum(EcoResCategoryHierarchyRole, NamedCategoryHierarchyRole)).value(enum2str(EcoResCategoryNamedHierarchyRole::RetailChannelNavigation));

    sysTableLookup.parmQuery(query);
    return sysTableLookup.performFormLookup();
}

