public int active()
{
    int ret;

    ret = super();

    if (RetailChannelPaymentConnectorLine.RecId == 0)
    {
        PaymentConnectorDetail.visible(false);
    }
    else
    {
        if (connectorSetup != null)
        {
            var merchant = RetailChannelPaymentConnectorLine.secureMerchantPropertiesEdit(false, '');
            connectorSetup.displayPropertyValuesFromXmlString(RetailChannelPaymentConnectorLine.PaymentConnectorName, merchant);
        }

        PaymentConnectorDetail.visible(true);
    }

    return ret;
}

public boolean validateWrite()
{
    boolean isValid = super();

    if (isValid)
    {
        if (connectorSetup != null)
        {
            isValid = !connectorSetup.validatePropertyValues(RetailChannelPaymentConnectorLine.PaymentConnectorName).displayErrors();
        }

        if (!isValid)
        {
            isValid = (Box::yesNo("@RET:Retail_Invalid_Payment_Setup", DialogButton::No) == DialogButton::Yes);
        }
    }

    return isValid;
}

public void write()
{
    // Save serialized properties
    var merchant = connectorSetup.getPropertyValuesAsXmlString(RetailChannelPaymentConnectorLine.PaymentConnectorName);
    RetailChannelPaymentConnectorLine.secureMerchantPropertiesEdit(true, merchant);

    // Since changes might have been made to Merchant Account properties
    // in the PaymentConnectorProperties field, we'll flush the cache.
    CreditCard::clearMerchantAccountPropertiesCache();

    super();
}

