public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    SysReferenceTableLookup sysTableLookup;
    Query query;
    QueryBuildDataSource qbDS;
    str filterTemplate = '((%1 == %2::%3) || (%1 == %2::%4) || (%1 == %2::%5))';

    str filter = strFmt(filterTemplate,
        fieldStr(RetailChannelProfile, ChannelProfileType),
        enumStr(RetailChannelProfileType),
        enum2Symbol(enumNum(RetailChannelProfileType), RetailChannelProfileType::SharePointOnlineStore),
        enum2Symbol(enumNum(RetailChannelProfileType), RetailChannelProfileType::AmazonOnlineStore),
        enum2Symbol(enumNum(RetailChannelProfileType), RetailChannelProfileType::eBayOnlineStore));

    sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(RetailChannelProfile), _formReferenceControl, true);
    sysTableLookup.addLookupfield(fieldNum(RetailChannelProfile, Name));

    query = new Query();
    qbDS = query.addDataSource(tableNum(RetailChannelProfile));

    qbDS.addRange(fieldNum(RetailChannelProfile, ChannelProfileType)).value(filter);

    sysTableLookup.parmQuery(query);

    return sysTableLookup.performFormLookup();
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    // For setting live channel database:
    //   Only show the channel databases that actually contain this channel

    SysReferenceTableLookup sysTableLookup;
    Query query;
    QueryBuildDataSource qbdsDataStore, qbdsDataStoreChannel;

    sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(RetailConnDatabaseProfile), _formReferenceControl, true);
    sysTableLookup.addLookupfield(fieldNum(RetailConnDatabaseProfile, Name));

    query = new Query();
    qbdsDataStore = query.addDataSource(tableNum(RetailConnDatabaseProfile));
    qbdsDataStoreChannel = qbdsDataStore.addDataSource(tableNum(RetailCDXDataStoreChannel), tableStr(RetailCDXDataStoreChannel));
    qbdsDataStoreChannel.relations(true);
    qbdsDataStoreChannel.joinMode(JoinMode::ExistsJoin);

    qbdsDataStoreChannel.addRange(fieldNum(RetailCDXDataStoreChannel, Channel)).value(queryValue(RetailOnlineChannelTable.RecId));

    sysTableLookup.parmQuery(query);

    return sysTableLookup.performFormLookup();
}

public void executeQuery()
{
    LogisticsLocation logisticsLocation;

    if (RetailOnlineChannelTable.RecId)
    {
        changeCompany(RetailOnlineChannelTable.inventLocationDataAreaId)
        {
            logisticsLocation = InventLocation::getDefaultLogisticsLocation_IN(RetailOnlineChannelTable.inventLocation, LogisticsLocationRoleType::Delivery);
            this.query().dataSourceTable(tableNum(TaxInformation_IN)).findRange(
                fieldNum(TaxInformation_IN, RegistrationLocation)).value(
                    queryValue(logisticsLocation.RecId));
        }
    }
    super();
}

public void init()
{
    super();
    this.query().dataSourceTable(tableNum(TaxInformation_IN)).addRange(
        fieldNum(TaxInformation_IN, IsPrimary)).value(queryValue(NoYes::Yes));
    this.query().dataSourceTable(tableNum(TaxInformation_IN)).addRange(
        fieldNum(TaxInformation_IN, RegistrationLocation));
}

