void clicked()
{
    super();

    if (connectorSetup != null)
    {
        RetailChannelPaymentConnectorLine.PaymentConnectorName = connectorSetup.getName(PaymentConnectorName.selection() + 1);

        var merchant = connectorSetup.getPropertiesAsXmlString(RetailChannelPaymentConnectorLine.PaymentConnectorName);
        RetailChannelPaymentConnectorLine.secureMerchantPropertiesEdit(true, merchant);
        RetailChannelPaymentConnectorLine.OnlineChannel = RetailOnlineChannelTable.RecId;
        connectorSetup.displayPropertyValuesFromXmlString(RetailChannelPaymentConnectorLine.PaymentConnectorName, merchant);
    }

    PaymentConnectorDetail.visible(true);

    RetailChannelPaymentConnectorLine.insert();
    RetailChannelPaymentConnectorLine_ds.research(false);
}

void clicked()
{
    super();

    RETAILCHANNELPAYMENTConnectorLine.delete();
    RETAILCHANNELPAYMENTConnectorLine_DS.research(false);

    if (RetailChannelPaymentConnectorLine.RecId == 0)
    {
        PaymentConnectorDetail.visible(false);
    }
    else
    {
        PaymentConnectorDetail.visible(true);

        var propertyXML = "";

        if (connectorSetup != null)
        {
            if (RetailChannelPaymentConnectorLine.SecureMerchantProperties != conNull())
            {
                propertyXML = RetailChannelPaymentConnectorLine.secureMerchantPropertiesEdit(false, '');
            }
            else
            {
                propertyXML = RetailChannelPaymentConnectorLine.PaymentConnectorProperties;
            }

            connectorSetup.displayPropertyValuesFromXmlString(RetailChannelPaymentConnectorLine.PaymentConnectorName, propertyXML);
        }
    }
}

