void init()
{
    super();

    qbdsRetailSalesTrans = RetailTransactionSalesTrans_ds.query().dataSourceTable(tablenum(RetailTransactionSalesTrans));
    qbdsRetailTransTable = RetailTransactionTable_ds.query().dataSourceTable(tablenum(RetailTransactionTable));
    qbdsEcoResCategory = EcoResCategory_ds.query().dataSourceTable(tablenum(EcoResCategory));

    backOrderDateSalesLine = qbdsRetailSalesTrans.addRange(fieldnum(RetailTransactionSalesTrans, Transdate));

    retailitemrange = qbdsRetailSalesTrans.addRange(fieldnum(RetailTransactionSalesTrans, Itemid));

    transstatus = qbdsRetailSalesTrans.addRange(fieldNum(RetailTransactionSalesTrans, TransactionStatus));
    entrystatus = qbdsRetailTransTable.addRange(fieldNum(RetailTransactionTable, EntryStatus));
    qbdsRetailTransTable.addRange(fieldNum(RetailTransactionTable, Type)).value(SysQuery::valueNot(enum2Str(RetailTransactionType::SuspendedTransaction)));

    statusfrom = RetailEntryStatus::None;
    statusto   = RetailEntryStatus::Posted;

    qbdsRetailSalesTrans.addSortField(fieldnum(RetailTransactionSalesTrans, Transdate));
    qbdsEcoResCategory.enabled(false);
    qbdsEcoResCategory.clearRanges();

    backorderDate.dateValue(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));
    DateTo.dateValue(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));
    timeedit.value(0);
    timeedit1.value(86400);
    element.updateDesign(InventDimFormDesignUpdate::Init);

    viewInventTrans = false;
    tabSalesLine.visible(true);
    categoryReference.enabled(true);
    Item.enabled(true);
}

