        void executeQuery()
        {
            if(backorderDate.dateValue() == dateTo.dateValue()) //If fromDate equals to toDate, select transactions from fromTime to toTime on fromDate.
            {
                backorderDateSalesLine.value(strFmt('(%1 == %2) && (%3 >= %4) && (%3 <= %5)',
                    fieldStr(RetailTransactionSalesTrans, transDate), date2StrXpp(backOrderDate.dateValue()),
					fieldStr(RetailTransactionSalesTrans, transTime), timeedit.value(), timeedit1.value()));
            }
            else if (backorderDate.dateValue() < dateTo.dateValue()) //If fromDate less than toDate, select transactions from fromDate + fromTime to toDate + toTime.
            {
                backorderDateSalesLine.value(strFmt('((%1 > %2) && (%1 < %3)) || ((%1 == %2) && (%4 >= %5)) || ((%1 == %3) && (%4 <= %6))',
                    fieldStr(RetailTransactionSalesTrans, transDate), date2StrXpp(backOrderDate.dateValue()), date2StrXpp(dateto.dateValue()),
					fieldStr(RetailTransactionSalesTrans, transTime), timeedit.value(), timeedit1.value()));
            }
            else
            {
				backorderDateSalesLine.value(strFmt('%1..%2',backOrderDate.dateValue(), dateto.dateValue()));
            }

            retailitemrange.value(strfmt(item.text()+'*'));

            if (TransactionViewRadioButton.selection() == 2)
            {
                transstatus.value(strfmt(int2str(RetailEntryStatus::Posted)));
                entrystatus.value(strfmt(int2str(RetailEntryStatus::Posted)));
                menubutton1.enabled(true);
            }
            else if (TransactionViewRadioButton.selection() == 1)
            {
                transstatus.value(strfmt(int2str(RetailEntrystatus::None)));
                entrystatus.value(strfmt(int2str(RetailEntryStatus::None)));
                menubutton1.enabled(false);
            }
            else if (TransactionViewRadioButton.selection() == 0)
            {
                transstatus.value(strfmt("%1, %2",int2str(RetailEntryStatus::None), int2str(RetailEntryStatus::Posted)));
                entrystatus.value(strfmt("%1, %2",int2str(RetailEntryStatus::None), int2str(RetailEntryStatus::Posted)));
                menubutton1.enabled(true);
            }

            super();

            // LS.GV.8.01.2007_Start
            tTaxAmount = RetailTransactionSalesTrans::FromTototalTaxAmount(
                categoryReference.value(),
                Item.text(),
                BackorderDate.dateValue(),
                Dateto.dateValue(),
                timeedit.value(),
                timeedit1.value(),
                statusfrom,
                statusto);
            tCostAmount = RetailTransactionSalesTrans::FromTototalCostAmount(
                categoryReference.value(),
                Item.text(),
                BackorderDate.dateValue(),
                Dateto.dateValue(),
                timeedit.value(),
                timeedit1.value());
            tDiscAmount = RetailTransactionSalesTrans::FromTototalDiscAmount(
                categoryReference.value(),
                Item.text(),
                BackorderDate.dateValue(),
                Dateto.dateValue(),
                timeedit.value(),
                timeedit1.value(),
                statusfrom,
                statusto);
            tNetAmount = RetailTransactionSalesTrans::FromTototalNetAmount(
                categoryReference.value(),
                Item.text(),
                BackorderDate.dateValue(),
                Dateto.dateValue(),
                timeedit.value(),
                timeedit1.value(),
                statusfrom,
                statusto);
            tQty = RetailTransactionSalesTrans::FromTototalQty(
                categoryReference.value(),
                Item.text(),
                BackorderDate.dateValue(),
                Dateto.dateValue(),
                timeedit.value(),
                timeedit1.value(),
                statusfrom,
                statusto);
            // LS.GV.8.01.2007_End

            salesLine1QueryExecuted = true;
        }

