    Common ret;

    ret  = EcoResCategory::lookupCategoryHierarchyRole(this, EcoResCategoryNamedHierarchyRole::Retail);

    return ret;
}

public boolean modified()
{
    boolean ret;
    EcoResCategoryId categoryId;
    EcoResCategory category;

    ret = super();

    categoryId = this.value();

    if (categoryId)
    {
        category = EcoResCategory::find(categoryId);
        EcoResCategory_ds.query().dataSourceTable(tablenum(EcoResCategory)).enabled(true);
        EcoResCategory_ds.query().dataSourceTable(tablenum(EcoResCategory)).clearRanges();
        EcoResCategory_ds.query().dataSourceTable(tablenum(EcoResCategory))
            .addRange(fieldNum(EcoResCategory, NestedSetLeft))
            .value(queryRange(category.NestedSetLeft, 0));
        EcoResCategory_ds.query().dataSourceTable(tablenum(EcoResCategory))
            .addRange(fieldNum(EcoResCategory, NestedSetRight))
            .value(queryRange(0, category.NestedSetRight));
    }
    else
    {
        EcoResCategory_ds.query().dataSourceTable(tablenum(EcoResCategory)).enabled(false);
    }

    return ret;
}

