public int selectionChange()
{
    int             ret;

    ret = super();

    viewInventTrans = false;
    tabSalesLine.visible(true);
    categoryReference.enabled(true);
    Item.enabled(true);

    if (TransactionViewRadioButton.selection() == 2)
    {
        menubutton1.enabled(true);
        statusfrom = RetailEntryStatus::Posted;
        statusto   = RetailEntryStatus::Posted;
    }
    else if (TransactionViewRadioButton.selection() == 1)
    {
        menubutton1.enabled(false);
        statusfrom = RetailEntryStatus::None;
        statusto   = RetailEntryStatus::None;
    }
    else if (TransactionViewRadioButton.selection() == 0)
    {
        menubutton1.enabled(true);
        statusfrom = RetailEntryStatus::None;
        statusto   = RetailEntryStatus::Posted;
    }

    return ret;
}

