public void init()
{
    this.numberSeqPreInit();

    super();

    OmniChannelPaymentsDescriptionHtmlView.parmHtml(strFmt(
        "<html><body>%1&nbsp;<a target='_blank' href='https://go.microsoft.com/fwlink/?linkid=2049779'>%2</a></body></html>",
        "@Retail:RetailOmniChannelPaymentTokenizationText",
        "@Retail:RetailOmniChannelPaymentsLinkText"));

    str bingBodyText; 

    if (RetailDeploymentUtility::getCurrentCloud() == RetailDeploymentUtility::AzureChina)
    {
        str bingPrivacyText = strFmt("<a target='_blank' href='%2'>%1</a>", "@Retail:RetailBingMapsEnablementLinkText", "@Retail:RetailBingMapsPrivacyStatementLink");
        str bingDocsText = strFmt("<a target='_blank' href='%2'>%1</a>", "@Retail:RetailBingMapsEnablementConsultLinkText", "@Retail:RetailBingMapsDocsLink");
        str cloudSpecificAddition = strFmt(' %1 ', "@Retail:RetailBingMapsMooncakeAddition");
        bingBodyText = strFmt("@Retail:RetailBingMapsEnablementText", bingPrivacyText, bingDocsText, cloudSpecificAddition);
    }
    else
    {
        str bingDocumentation = strFmt("<a target='_blank' href='%2'>%1</a>", "@Retail:RetailBingMapsDocumentationText", "@Retail:RetailBingMapsDocumentationLink");
        str bingPrivacyAndCookies = strFmt("<a target='_blank' href='%2'>%1</a>", "@Retail:RetailBingMapsPrivacyAndCookies", "@Retail:RetailBingMapsPrivacyStatementLink");
        bingBodyText = strFmt("@Retail:RetailBingMapsUSNoticeConsentDisclosure", bingDocumentation, bingPrivacyAndCookies);
    }

    BingMapsDescriptionHtmlView.parmHtml(strFmt("<html><body>%1</body></html>", bingBodyText));

    RetailSharedParameters sharedParms = RetailSharedParameters::find();
    RetailSharedParameters_EnableLocalStorageBackup.visible(sharedParms.EnableFiscalIntegration);

    Copilot.visible(RetailStoreCommerceCopilotFeature::isEnabled());

}

void numberSeqPreInit()
{
    runExecuteDirect   = false;

    // Retail has 4 NumberSequenceModules in total
    // Each module may contain global-scope number sequences
    numberSequenceModules = [NumberSeqModule::RetailParameters, NumberSeqModule::RetailEx1, NumberSeqModule::RetailParametersEx2, NumberSeqModule::RBOStore, NumberSeqModule::CustHierarchy];
    scope = NumberSeqScopeFactory::createGlobalScope();
    NumberSeqApplicationModule::createReferencesMulti(numberSequenceModules, scope);
    tmpIdRef.setTmpData(NumberSequenceReference::configurationKeyTableMulti(numberSequenceModules));
}

