/// <summary>
/// User has modified the ClxStorageSasKeyLifetimeInHours field.
/// </summary>
public void modified()
{
    if (RetailSharedParameters.ClxStorageSasKeyLifetimeInHours < 1 )
    {
        RetailSharedParameters.ClxStorageSasKeyLifetimeInHours = RetailMediaHelper::DefaultClxSasKeyLifetimeInHours;;
        info("Clx storage Sas key lifetime can't be less than one hour.");
    }
}

/// <summary>
/// User has modified the ClxStorageSasKeyRecycleWindowInHours field.
/// </summary>
public void modified()
{
    if (RetailSharedParameters.ClxStorageSasKeyRecycleWindowInHours < 1 ||
            RetailSharedParameters.ClxStorageSasKeyRecycleWindowInHours > RetailSharedParameters.ClxStorageSasKeyLifetimeInHours)
    {
        RetailSharedParameters.ClxStorageSasKeyRecycleWindowInHours = RetailMediaHelper::DefaultClxSasKeyRecycleWindowInHours;
        info("Clx storage Sas key recyle window can't be less than one hour or more than the Clx storage Sas key lifetime.");
    }
}

