public int active()
{
    int ret;

    ret = super();

    if (RetailIdentityProvider.RecId)
    {
        OpenIDRelyingPartiesBody.enabled(true);
        identityProviderNewRecord = false;
        RetailRelyingParty_DS.executeQuery();
    }

    this.enableEditDelete();

    return ret;
}

private void enableEditDelete()
{
    RetailScaleUnitConfigurationMap configurationMap = RetailIdentityProvider;
    boolean allowEditDeleteScaleUnitConfiguration = !configurationMap.RetailScaleUnitConfigurationMap::isScaleUnitSystemRecord();

    RetailIdentityProvider_DS.allowEdit(allowEditDeleteScaleUnitConfiguration);
    RetailIdentityProvider_DS.allowDelete(allowEditDeleteScaleUnitConfiguration);
}

/// <summary>
/// Validates to prompt the retail identity provider before editing or deleting it.
/// </summary>
/// <returns>Alwasy default.</returns>
public boolean validate()
{
    boolean ret;

    RetailIdentityProvider identityProvider;

    select firstonly identityProvider
        where identityProvider.Issuer == RetailIdentityProvider.orig().Issuer;

    if (identityProvider.RecId)
    {
        warning("@Retail:RetailIdentityProviderChangePrompt");
    }

    ret = super();

    return ret;
}

public void write()
{
    if (RetailIdentityProvider.RecId)
    {
        RetailIdentityProvider retailIdentityProvider_current;
        retailIdentityProvider_current = RetailIdentityProvider_DS.cursor();
        RetailRelyingParty.ProviderId = retailIdentityProvider.RecId;
        super();
    }
}

public void init()
{
    super();
    filterRelyingPartyRange = this.query().dataSourceTable(tablenum(RetailRelyingParty)).addRange(fieldnum(RetailRelyingParty, ProviderId));
}

public void executeQuery()
{
    if (identityProviderNewRecord)
    {
        filterRelyingPartyRange.value(int642Str(0));
    }
    else
    {
        filterRelyingPartyRange.value(int642Str(RetailIdentityProvider_DS.cursor().RecId));
    }

    super();
}

/// <summary>
/// Retrieves data from joined data sources when a user navigates to a new record and then sets the new record as the current record.
/// This can take place when a user changes (by nagivating from one row to another) selected row in RelyingParty grid.
/// </summary>
/// <returns>Always returns 1.</returns>
public int active()
{
    int ret;

    ret = super();

    // Server Resource is only available for access_token issued by those providers we support:
    // AAD and AAD B2C access_token.
    boolean enabled = ((RetailIdentityProvider.Type == RetailIdentityProviderType::Aad) ||
                       (RetailIdentityProvider.Type == RetailIdentityProviderType::AadSocialAccessToken)) && RetailRelyingParty.RecId;
    ServerResourcesBody.enabled(enabled);

    this.enableEditDelete();

    return ret;
}

/// <summary>
/// Deletes the current record from the data source. Which can happen, when, for instance, a user deletes a row from RelyingParty grid.
/// </summary>
public void delete()
{
    super();
    ServerResourcesBody.enabled(false);
}

private void enableEditDelete()
{
    RetailScaleUnitConfigurationMap configurationMap = RetailRelyingParty;
    boolean allowEditDeleteScaleUnitConfiguration = !configurationMap.RetailScaleUnitConfigurationMap::isScaleUnitSystemRecord();

    RetailRelyingParty_DS.allowEdit(allowEditDeleteScaleUnitConfiguration);
    RetailRelyingParty_DS.allowDelete(allowEditDeleteScaleUnitConfiguration);
}

