public void write()
{
    TenantID tenantId = InventInventoryDataServiceParameters.AadAppTenantId;
    SysAADClientId clientId = InventInventoryDataServiceParameters.AadAppClientId;
    Name clientSecretName = InventInventoryDataServiceParameters.AadAppClientSecretName;

    InventInventoryDataServiceParameters.reread();

    InventInventoryDataServiceParameters.AadAppTenantId = tenantId;
    InventInventoryDataServiceParameters.AadAppClientId = clientId;
    InventInventoryDataServiceParameters.AadAppClientSecretName = clientSecretName;

    super();
}

