/// <summary>
/// Changing which product availability table we update, we will also set the cache value's to their default.
/// </summary>
/// <returns>Flag indicating if selection change was completed.</returns>
public int selectionChange()
{
    int ret;
    boolean confirmSelectionChange = false;

    int secondsCacheLastProductAvailabilityJob = 180;
    int secondsCacheLastSumNewTransactions = 60;
    int secondsCacheLastSumNewTransactionsEcommerce = 0;

    // Provide a warning text for effects of the parameter change.
    confirmSelectionChange = Box::okCancel("@Retail:ProductAvailabilityJobControlModelUpdateConfirm", DialogButton::Cancel) == DialogButton::Ok;

    // If the user clicked 'Ok' and not 'Cancel'.
    if (confirmSelectionChange)
    {
        // Alter the ProductAvailabilityJobPolicy column to reflect changes in the UI.
        ret = super();

        // If we are changing from Legacy Product Availability to Modern, then we will set our default caching time parameters.
        RetailProductAvailabilityJobControlModel newValue;
        newValue = this.selection();
        if (newValue == RetailProductAvailabilityJobControlModel::PopulateRetailProductWarehouseInventoryTable)
        {
            // Inventory Availability on Channel side uses the last value brought from CDX (From Product Availability Job).
            // Then, it takes the Sum of transacations that have not been included in that last calculation.
            // This parameter determines the cache expiration period for checking the last value brought from CDX.
            RetailSharedParameters.InventoryAvailabilityCachePeriod = secondsCacheLastProductAvailabilityJob;

            // This parameter determines the cache expiration period for checking if more uncounted sales transactions have occurred.
            RetailSharedParameters.InventoryAvailabilitySumDeltaCachePeriod = secondsCacheLastSumNewTransactions;

            // This parameter determines the cache expiration period for checking if more uncounted sales transactions have occurred in e-Commerce.
            RetailSharedParameters.InventoryAvailabilitySumDeltaCachePeriodEcommerce = secondsCacheLastSumNewTransactionsEcommerce;
        }
    }
    else
    {
        ret = false;
    }

    return ret;
}

public void lookup()
{
    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(KeyVaultCertificateTable), this);
    sysTableLookup.addLookupField(fieldNum(KeyVaultCertificateTable, Name));
    sysTableLookup.addLookupField(fieldNum(KeyVaultCertificateTable, Description));

    Query query = new Query();
    QueryBuildDataSource queryBuildDataSource = query.addDataSource(tableNum(KeyVaultCertificateTable));
    queryBuildDataSource.addRange(fieldNum(KeyVaultCertificateTable, KeyVaultCertificateType)).value(SysQuery::value(KeyVaultCertificateType::Manual));

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

