private void initDesign_RU()
{
    RetailParameters retailParameters = RetailParameters::find();

    LedgerJournalTable_GiftCardPaymentJournal_RU.visible(
        retailParameters.ProcessGiftCardsAsPrepayments_RU &&
        retailParameters.GiftCardCompany != ''            &&
        retailParameters.GiftCardCompany != curext());
}

/// <summary>
/// Shows gift card customer payment journal in the gift card company
/// </summary>
public void showGiftCardPaymentJournal_RU()
{
    Args                args;
    FormRun             formRun;
    MenuFunction        menuFunction;
    LedgerJournalTable  giftCardJournal_RU;
    LedgerJournalTable  storeCompanyJournal;
    DataAreaId          giftCardCompany = RetailParameters::find().GiftCardCompany;

    select firstonly OriginalCompany, OriginalJournalNum from storeCompanyJournal
        where storeCompanyJournal.OriginalJournalNum != ''              &&
              storeCompanyJournal.OriginalCompany    == giftCardCompany &&
              storeCompanyJournal.RetailStatementId  == RetailStatementJour.StatementId;

    if (storeCompanyJournal.OriginalJournalNum &&
        xDataArea::exist(giftCardCompany))
    {
        changeCompany(giftCardCompany)
        {
            giftCardJournal_RU = LedgerJournalTable::find(storeCompanyJournal.OriginalJournalNum);

            args = new Args();
            args.caller(element);
            args.record(giftCardJournal_RU);
            menuFunction = new MenuFunction(menuitemdisplaystr(LedgerJournalTable_CustPaym), MenuItemType::Display);
            formRun = menuFunction.create(args);
            formRun.run();
            formRun.wait();
        }
    }
}

public void run()
{
    super();

    if (element.args() && element.args().record()
        && element.args().record().TableId == tableNum(RetailStatementJour))
    {
        RetailStatementJour_DS.positionToRecord(element.args().record());
    }
}

public int active()
{
    int ret;

    ret = super();

    TabLine.visible(RetailStatementJour.StatementType != RetailStatementType::Transactional);

    RetailCashDeclaration.enabled(false);

    RetailStatementTrans_ds.object(fieldNum(RetailStatementTrans, terminalId)).visible(false);
    RetailStatementTrans_ds.object(fieldNum(RetailStatementTrans, staffId)).visible(false);
    RetailStatementTrans_ds.object(fieldNum(RetailStatementTrans, uniqueShiftId)).visible(false);
    switch (RetailStatementJour_ds.object(fieldNum(RetailStatementJour, statementMethod)).getValue())
    {
        case RetailStaffTerminalTotal::Terminal:
            RetailStatementTrans_ds.object(fieldNum(RetailStatementTrans, terminalId)).visible(true);
            break;
        case RetailStaffTerminalTotal::Staff:
            RetailStatementTrans_ds.object(fieldNum(RetailStatementTrans, staffId)).visible(true);
            break;
        case RetailStaffTerminalTotal::Shift:
            RetailStatementTrans_ds.object(fieldNum(RetailStatementTrans, uniqueShiftId)).visible(true);
            break;
    }

    return ret;
}

public int active()
{
    int ret;

    ret = super();

    if (RetailStoreTenderTypeTable::find(RetailStoreTable::find(RetailStatementJour.storeId).RecId, RetailStatementTrans.tenderTypeId).countingRequired == true)
        RetailCashDeclaration.enabled(true);
    else
        RetailCashDeclaration.enabled(false);

    return ret;
}

void clicked()
{
    RetailTransactionTable transactionTable;

    select count(recId) from transactionTable
        where transactionTable.statementId == RetailStatementJour.statementId
           && transactionTable.type == RetailTransactionType::PhysicalInventory;

    if (transactionTable.recId)
    {
        if (!RetailStatementJour.numberOfUnpostedInventTrans())
        {
            if (Box::yesNo("@RET4194", DialogButton::No) == DialogButton::No)
            {
                throw error("@RET4195");
            }
        }

        RetailInventoryFunctions::createForPostedStatement(RetailStatementjour.statementId);
    }
    else
    {
        throw error("@RET4196");
    }

    info("@RET4197");
}
