}

/// <summary>
/// Enable or disable post, calculate or clear buttons.
/// </summary>
public void resetButtons()
{
    boolean hasBeenCalculated = statementTable.calculatedDate != dateNull();
    boolean hasBeenPosted = statementTable.postedDate != dateNull();
    boolean hasBeenThroughPosting = statementTable.postingDate != dateNull();

    RetailStatementCalculate.enabled(!hasBeenCalculated);
    RetailStatementPost.enabled(hasBeenCalculated && !hasBeenPosted);
    RetailStatementClear.enabled(hasBeenCalculated && (!hasBeenThroughPosting));
}

