public int active()
{
    int ret;

    ret = super();

    statementTable_ds.object(fieldnum(RetailStatementTable, postingDate)).allowEdit(true);
    statementTable_ds.object(fieldnum(RetailStatementTable, transFromDate)).allowEdit(true);
    statementTable_ds.object(fieldnum(RetailStatementTable, transToDate)).allowEdit(true);
    statementTable_ds.object(fieldnum(RetailStatementTable, transFromTime)).allowEdit(true);
    statementTable_ds.object(fieldnum(RetailStatementTable, transToTime)).allowEdit(true);

    StatementTable_DS.object(fieldnum(RetailStatementTable, closingMethod)).allowEdit(StatementTable.statementMethod != RetailStaffTerminalTotal::Staff);

    ButtonRBOCashDeclaration.enabled(false);

    statementLine_ds.object(fieldNum(RetailStatementLine, terminalId)).visible(false);
    statementLine_ds.object(fieldNum(RetailStatementLine, staffId)).visible(false);
    statementLine_ds.object(fieldNum(RetailStatementLine, uniqueShiftId)).visible(false);
    switch (statementTable_ds.object(fieldNum(RetailStatementTable, statementMethod)).getValue())
    {
        case RetailStaffTerminalTotal::Terminal:
            statementLine_ds.object(fieldNum(RetailStatementLine, terminalId)).visible(true);
            break;
        case RetailStaffTerminalTotal::Staff:
            statementLine_ds.object(fieldNum(RetailStatementLine, staffId)).visible(true);
            break;
        case RetailStaffTerminalTotal::Shift:
            statementLine_ds.object(fieldNum(RetailStatementLine, uniqueShiftId)).visible(true);
            break;
    }

    return ret;
}

