/// <summary>
/// Gives a list of lookup result when user clicks the lookup dropdown.
/// </summary>
/// <param name = "_formControl">The control source for the lookup method.</param>
/// <param name = "_filterStr">The filter object.</param>
public void lookup(FormControl _formControl, str _filterStr)
{
    int64 storeRecordId;
    str storeId;
    RetailStaffTerminalTotal statementMethod;
    RetailStoreTable storeTable;

    // Filter terminals and staffs that are associated with the store identifier only.
    statementMethod = statementTable_DS.object(fieldNum(RetailStatementTable, statementMethod)).getValue();
    storeId = statementTable_DS.object(fieldNum(RetailStatementTable, storeId)).getValue();
    storeTable = RetailStoreTable::find(storeId);
    storeRecordId = storeTable.RecId;

    if (!storeRecordId)
    {
        return;
    }

    if (statementMethod == RetailStaffTerminalTotal::Terminal)
    {
        this.terminalLookup(_formControl, storeRecordId);
    }
    else if (statementMethod == RetailStaffTerminalTotal::Staff)
    {
        this.staffLookup(_formControl, storeRecordId);
    }

    // No lookup would be shown for other statement method types.
}

/// <summary>
/// Gives a list of lookup for staffs that has address books correspond to the Store's address books on the current statement.
/// </summary>
/// <param name = "_formControl">The control source for the staff lookup.</param>
/// <param name = "_storeRecordId">The record identifier of the store.</param>
private void staffLookup(FormControl _formControl, int64 _storeRecordId)
{
    Query query = new Query();
    QueryBuildDataSource workerQueryDataSource, channelAddressBookQueryDataSource,
        partyAddressBookQueryDataSource, staffQueryDataSource;
    QueryBuildRange queryBuildRange;

    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(RetailStaffTable), _formControl);
    sysTableLookup.addLookupField(fieldNum(RetailStaffTable, staffId));
    sysTableLookup.addLookupField(fieldNum(RetailStaffTable, employmentType));

    // Find staffs that has any address books correspond to the Store's address books.
    // E.g. StoreA has address books AreaA, AreaC, AreaD
    // Worker1 has address books AreaA, AreaE
    // Worker2 has address books AreaB
    // Worker3 has address books AreaD
    // Result: Worker1 and Worker3 will be included on the result.
    staffQueryDataSource = query.addDataSource(tableNum(RetailStaffTable));
    workerQueryDataSource = staffQueryDataSource.addDataSource(tableNum(HcmWorker));
    workerQueryDataSource.joinMode(JoinMode::ExistsJoin);
    workerQueryDataSource.addLink(fieldNum(HcmWorker, PersonnelNumber), fieldNum(RetailStaffTable, staffId));

    partyAddressBookQueryDataSource = workerQueryDataSource.addDataSource(
        tableNum(DirAddressBookParty));
    partyAddressBookQueryDataSource.joinMode(JoinMode::ExistsJoin);
    partyAddressBookQueryDataSource.addLink(fieldNum(HcmWorker, Person),
         fieldNum(DirAddressBookParty, Party));

    channelAddressBookQueryDataSource = partyAddressBookQueryDataSource.addDataSource(
        tableNum(RetailStoreAddressBook));
    channelAddressBookQueryDataSource.joinMode(JoinMode::ExistsJoin);
    channelAddressBookQueryDataSource.addLink(
        fieldNum(DirAddressBookParty, AddressBook),
        fieldNum(RetailStoreAddressBook, AddressBook));
    channelAddressBookQueryDataSource.addRange(fieldNum(RetailStoreAddressBook, StoreRecId))
        .value(int642Str(_storeRecordId));

    // filter address book type Employee
    channelAddressBookQueryDataSource.addRange(fieldNum(RetailStoreAddressBook, AddressBookType))
        .value(enum2Str(RetailAddressBookType::Employee));

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

/// <summary>
/// Gives a list of lookup for terminals that are associated with the store on the current statement.
/// </summary>
/// <param name = "_formControl">The control source for the terminal lookup.</param>
/// <param name = "_storeRecordId">The record identifier of the store.</param>
private void terminalLookup(FormControl _formControl, int64 _storeRecordId)
{
    Query query = new Query();
    QueryBuildDataSource queryBuildDataSource;
    QueryBuildRange queryBuildRange;

    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(RetailTerminalTable), _formControl);
    sysTableLookup.addLookupField(fieldNum(RetailTerminalTable, TerminalId));
    sysTableLookup.addLookupField(fieldNum(RetailTerminalTable, Name));

    queryBuildDataSource = query.addDataSource(tableNum(RetailTerminalTable));
    queryBuildRange = queryBuildDataSource.addRange(fieldNum(RetailTerminalTable, StoreRecId));
    queryBuildRange.value(int642Str(_storeRecordId));

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

