public void create(boolean _append = false)
{
    super(_append);
    statementLine.initFromStatementTable(statementTable);
}

public int active()
{
    int ret;

    ret = super();

    if (RetailStoreTenderTypeTable::find(RetailStoreTable::find(statementLine.storeId).RecId, statementLine.tenderTypeId).function == RetailTenderFunction::Card)
        statementLine_ds.object(fieldnum(RetailStatementLine, cardTypeId)).enabled(true);
    else
        statementLine_ds.object(fieldnum(RetailStatementLine, cardTypeId)).enabled(false);

    if (RetailStoreTenderTypeTable::find(RetailStoreTable::find(statementLine.storeId).RecId, statementLine.tenderTypeId).countingRequired == true)
    {
        ButtonRBOCashDeclaration.enabled(true);
        statementLine_ds.object(fieldnum(RetailStatementLine, countedAmount)).allowEdit(true);
    }
    else
    {
        ButtonRBOCashDeclaration.enabled(false);
        statementLine_ds.object(fieldnum(RetailStatementLine, countedAmount)).allowEdit(false);
    }

    if (statementLine.tenderDeclaration)
        ButtonRBOCashDeclaration.enabled(true);

    return ret;
}

public void modified()
{
    super();

    statementLine.calcDifferenceAmount();
}

public boolean validate()
{
    boolean ret;

    ret = super();

    if (RetailStoreTenderTypeTable::find(RetailStoreTable::find(statementLine.storeId).RecId, statementLine.tenderTypeId).function == RetailTenderFunction::Card)
        statementLine_ds.object(fieldnum(RetailStatementLine, cardTypeId)).enabled(true);
    else
    {
        statementLine_ds.object(fieldnum(RetailStatementLine, cardTypeId)).enabled(false);
        statementLine.cardTypeId = "";
    }

    return ret;
}

