private SalesLine constructSimpleSalesLine(ItemId _itemId)
{
    SalesLine salesLine;

    salesLine.ItemId = _itemId;
    salesLine.SalesUnit = InventTableModule::find(salesLine.ItemId, ModuleInventPurchSales::Sales).UnitId;
    salesLine.SalesQty = 1;

    return salesLine;
}

    public void init()
    {
        OMOperatingUnit operatingUnit;
        InventTable LocalInventTable;
        super();

        mapRetailVariantPPV             = new Map(Types::String, Types::String);
        mapRetailVariantPPVDayPrice     = new Map(Types::String, Types::Real);

        Args args = this.args();
        if (args && args.record())
        {
            params = args.parmObject();
            if (params)
            {
                calcStartDate = params.parmDateFrom();
                calcEndDate = params.parmDateTo();
            }

            formTableId = args.record().TableId;
            switch (formTableId)
            {
                case tableNum(OMOperatingUnit):
                    operatingUnit = element.args().record();
                    ALKPCG_ds.query().dataSourceTable(tableNum(RetailAssortmentLookupChannelGroup)).addRange(fieldNum(RetailAssortmentLookupChannelGroup, OMOperatingUnitId)).value(queryValue(operatingUnit.RecId));
                    break;
            }
        }

        // pPV_DS
        pPV_DS.cacheCalculateMethod('getDiscountPrice0');
        pPV_DS.cacheCalculateMethod('getDiscountPrice1');
        pPV_DS.cacheCalculateMethod('getDiscountPrice2');
        pPV_DS.cacheCalculateMethod('getDiscountPrice3');
        pPV_DS.cacheCalculateMethod('getDiscountPrice4');
        pPV_DS.cacheCalculateMethod('getDiscountPrice5');
        pPV_DS.cacheCalculateMethod('getDiscountPrice6');
        pPV_DS.cacheCalculateMethod('getDiscountPrice7');

        // set default active date to today
        activeDate = DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone());
        activeDateEdit.dateValue(activeDate);

        this.setPriceHistoryControlStatus();

		if (params.parmCheckInventoryAvailability())
        {
            QueryBuildDataSource qbds = Invent_ds.queryBuildDataSource().addDataSource(tablenum(InventSum));
            qbds.relations(true);
            qbds.addRange(fieldnum(InventSum, AvailPhysical)).value(strFmt('(%1.%2 > 0)', qbds.name(), fieldStr(InventSum, AvailPhysical)));
            qbds.joinMode(joinMode::ExistsJoin);
        }

        // set product translations to default language
        ProductTrans_ds.queryBuildDataSource()
            .addRange(fieldNum(EcoResProductTranslation, LanguageId))
            .value(SysQuery::value(LanguageTable::defaultLanguage()));

        using(var _ = RetailPricingInstrumentationSegment::construct('PriceList', 'executeQuery'))
        {
            retailAssortmentLookup_ds.executeQuery();
        }
    }

private void setPriceHistoryControlStatus()
{
    date tempDate = calcStartDate;
    for(int c = 9; c < 17; c++)
    {
        FormStringControl control = Grid.controlNum(c);
        control.label(strFmt('%1 %2',"@REX4040113", date2Str(tempDate, 21, DateDay::Digits2, DateSeparator::Hyphen, DateMonth::Digits2, DateSeparator::Hyphen, DateYear::Digits4)));

        if(tempDate > calcEndDate)
        {
            control.visible(false);
        }
        else
        {
            control.visible(true);
        }

        tempDate++;
    }
}

/// <summary>
/// Calculates the active price and discount amount.
/// </summary>
/// <param name="_assortmentLookup">
/// An <c>RetailAssortmentLookup</c> record to calculate price, discount and offer ids for product.
/// </param>
/// <default>
/// This function calculates the active price and discount amount and put them into caching.
/// </default>
public void calculateDisplayDetailsForVariant(RetailProductParentView _ppv)
{
    RetailAssortmentLookup          localAssortmentLookup = retailAssortmentLookup;
    RetailAssortmentLookupChannelGroup localRetailAssortmentLookupChannelGroup;
    SalesLine                       salesLine;
    InventTable                     localInventTable = invent;
    RetailChannelTable              localChannelTable = retailChannelTable;
    RetailProductParentView         localProductParent = _ppv;
    InventTableModule               localInventTableModule;
    InventDim                       localInventDim;
    InventDimCombination            localInventDimCombination = rIDimC, retailVariant;
    EcoResProductTranslation        localProdTranslation = productTrans;
    List                            priceDiscountLines, priceDiscountLinesProduct;
    ListEnumerator                  priceDiscountLinesEnumerator, priceDiscountLinesEnumeratorProduct;
    RetailPriceDiscountLine         priceDiscountLine;
    Map                             mapProductPromOffIds;
    Map                             mapProductNonPromOffIds;
    Map                             mapChannelId;
    MapEnumerator                   channelIdEnumerator;
    RefRecId                        assortmentLookUpRecId;
    int                             recordCount;
    int                             offsetInMinutes;
    boolean                         multipleStores;
    date                            tempDate;
    int                             days;
    boolean                         calculatePrice;
    utcdatetime                     transactionUTCTime;

    Set setProductsRecIds = new Set(Types::Int64);

    TmpRetailPriceCheckProduct      tmpRetailPriceCheckProduct;

    priceDiscountLines = new List(Types::Class);

    priceDiscountLinesProduct = new List(Types::Class);
    mapChannelId = new Map(Types::Int64, Types::Integer);
    calculatePrice = true;

    recordCount = PPV_ds.numberOfRowsLoaded();

    // Find out from where price list form is being called.
    // When called from product we get all channels having product.
    // When called from a channel we get products selling in those channels.
    // When called from catalog we will filter based on channel and have catalog products.
    for (i = 1; i <= recordCount; i++)
    {
        localProductParent = pPV_ds.cursor(i);

        str key = element.getKeyForPriceListForVariant(localProductParent);

        if (!mapRetailVariantPPV.exists(key))
        {
            calculatePrice = true;

            localAssortmentLookup = localProductParent.joinChild();

            localChannelTable = localProductParent.joinChild().joinChild().joinChild().joinChild();
            localInventTable = localChannelTable.joinChild();
            localInventDimCombination = localInventTable.joinChild();
            localInventDim = localInventDimCombination.joinChild();

            if (params.parmCheckInventoryAvailability())
            {
                if (!RetailPriceListParameters::checkInventoryAvailabilityForProduct(localChannelTable, localInventTable, localInventDim))
                {
                    calculatePrice = false;
                }
            }

            if (calculatePrice)
            {
                salesLine = this.constructSimpleSalesLine(localInventTable.ItemId);

                priceDiscountLine = new RetailPriceDiscountLine();
                priceDiscountLine.parmChannelRefRecId(localChannelTable.RecId);
                priceDiscountLine.parmInventDimId(localInventDimCombination.InventDimId);
                priceDiscountLine.parmRetailVariantId(localInventDimCombination.RetailVariantId);
                priceDiscountLine.parmItemId(localInventTable.ItemId);
                priceDiscountLine.parmQty(1);
                priceDiscountLine.parmReferenceRecId(localAssortmentLookup.RecId);
                priceDiscountLine.parmSalesUnit(salesLine.SalesUnit);

                priceDiscountLines.addEnd(priceDiscountLine);

                tempDate = calcStartDate;
                days = 0;

                while(tempDate <= calcEndDate)
                {
                    offsetInMinutes = DateTimeUtil::getTimeZoneOffset(tempDate, DateTimeUtil::getUserPreferredTimeZone());
                    transactionUTCTime = DateTimeUtil::addMinutes(tempDate, -offsetInMinutes);
                    // Calculate the price with the right time with the respect of the channel timezone.
                    RetailPricingCalculatorCreator::getInstance().createPriceCheck().calculateIndependentPriceDiscountsWithDateTime(localChannelTable.RecId, priceDiscountLines, RetailPricingEngineHelper::getSessionDateTimeInChannelTimeZone(localChannelTable.RecId, transactionUTCTime));

                    priceDiscountLinesEnumerator = priceDiscountLines.getEnumerator();
                    this.populatePriceDiscountsOfferIdsWithVariant(key, priceDiscountLinesEnumerator, mapProductPromOffIds, mapProductNonPromOffIds, int2Str(days));

                    tempDate++;
                    days++;
                }
            }

            mapRetailVariantPPV.insert(key, key);
        }
    }
}

private void populatePriceDiscountsOfferIdsWithVariant(str parentKey, ListEnumerator priceDiscountLinesEnumerator, Map mapProductPromOffIds, Map mapProductNonPromOffIds, str day)
{
    RefRecId assortmentLookUpRecId, channelRecId;
    RetailPriceDiscountLine priceDiscountLine;
    RetailAssortmentLookup          localAssortmentLookup = retailAssortmentLookup;
    InventTable                     localInventTable;
    RetailChannelTable              localChannelTable;
    CatalogProduct                  localCatalogProduct;
    RetailProductParentView         localProductParent = pPV;
    InventTableModule               localInventTableModule;
    InventDimCombination            localInventDimCombination;
    RetailChannelItemPriceAgreement localPriceAgreement;
    EcoResProductTranslation        localProdTranslation = productTrans;

    while (priceDiscountLinesEnumerator.moveNext())
    {
        priceDiscountLine = priceDiscountLinesEnumerator.current();

        assortmentLookUpRecId = priceDiscountLine.parmReferenceRecId();
        channelRecId = priceDiscountLine.parmChannelRefRecId();

        str ppvPriceKey = strFmt('%1-%2',parentKey, day);
        mapRetailVariantPPVDayPrice.insert(ppvPriceKey, priceDiscountLine.parmPrice() -  priceDiscountLine.parmDiscount());
    }
}

private str getKeyForPriceListForVariant(RetailProductParentView _ppv)
{
    return strFmt('%1-%2-%3',int642Str(retailAssortmentLookup.RecId), int642Str(_ppv.ParentProduct), int642Str(_ppv.Product));
}

