}

/// <summary>
/// Calculates the active price.
/// </summary>
/// <param name = "_query"> Query object that has to be iterated and prepare item data.</param>
private void calculateDisplayDetailsForCallerOperatingUnit(Query _query)
{
    RetailAssortmentLookup          localAssortmentLookup;
    SalesLine                       salesLine;
    InventTable                     localInventTable;
    RetailChannelTable              localChannelTable;
    RetailProductParentView         localProductParent ;
    InventTableModule               localInventTableModule;
    InventDim                       localInventDim;
    InventDimCombination            localInventDimCombination = rIDimC, retailVariant;
    List                            priceDiscountLines;
    ListEnumerator                  priceDiscountLinesEnumerator;
    RetailPriceDiscountLine         priceDiscountLine;
    int                             offsetInMinutes;
    date                            tempDate;
    int                             days;
    boolean                         calculatePrice;
    utcdatetime                     transactionUTCTime;
    RefRecId                        channelRecId;

    priceDiscountLines = new List(Types::Class);

    calculatePrice = true;
    QueryRun queryRun = new QueryRun(_query);
    listOfDisplayDetails = new RecordInsertList(tableNum(TmpRetailPriceListDisplayValues),
                                                false, // skip insert
                                                false, // skip database log
                                                false, // skip events
                                                false, // skip aos validation
                                                false, // skip RLS validation
                                                tmpRetailPriceListDisplayValues);

    while (queryRun.next())
    {
        calculatePrice = true;

        localProductParent = queryRun.get(tableNum(RetailProductParentView));
        localAssortmentLookup = queryRun.get(tableNum(RetailAssortmentLookup));
        localChannelTable = queryRun.get(tableNum(RetailChannelTable));
        localInventTable = queryRun.get(tableNum(InventTable));
        localInventDimCombination = queryRun.get(tableNum(InventDimCombination));
        localInventDim = queryRun.get(tableNum(InventDim));

        if ( params.parmCheckInventoryAvailability()
                && !RetailPriceListParameters::checkInventoryAvailabilityForProduct(localChannelTable, localInventTable, localInventDim))
        {
            calculatePrice = false;
        }

        if (calculatePrice)
        {
            salesLine = this.constructSimpleSalesLine(localInventTable.ItemId);

            priceDiscountLine = new RetailPriceDiscountLine();
            priceDiscountLine.parmChannelRefRecId(localChannelTable.RecId);
            priceDiscountLine.parmInventDimId(localInventDimCombination.InventDimId);
            priceDiscountLine.parmRetailVariantId(localInventDimCombination.RetailVariantId);
            priceDiscountLine.parmItemId(localInventTable.ItemId);
            priceDiscountLine.parmQty(1);
            priceDiscountLine.parmReferenceRecId(localAssortmentLookup.RecId);
            priceDiscountLine.parmSalesUnit(salesLine.SalesUnit);

            priceDiscountLines.addEnd(priceDiscountLine);

            channelRecId = localChannelTable.RecId;
        }
    }

    tempDate = calcStartDate;
    days = 0;

    while(tempDate <= calcEndDate && days < 8)
    {
        offsetInMinutes = DateTimeUtil::getTimeZoneOffset(tempDate, DateTimeUtil::getUserPreferredTimeZone());
        transactionUTCTime = DateTimeUtil::addMinutes(tempDate, -offsetInMinutes);

        // Calculate the price with the right time with the respect of the channel timezone.
        RetailPricingCalculatorCreator::getInstance().createPriceCheck().calculateIndependentPriceDiscountsWithDateTime(channelRecId, priceDiscountLines, RetailPricingEngineHelper::getSessionDateTimeInChannelTimeZone(channelRecId, transactionUTCTime));

        priceDiscountLinesEnumerator = priceDiscountLines.getEnumerator();
        this.populatePriceDiscountsOfferIdsWithVariantInTmpTable(priceDiscountLinesEnumerator,days);

        tempDate++;
        days++;
    }

    listOfDisplayDetails.insertDatabase();
}

/// <summary>
/// Populate temp table with active prices from discount line enumerator.
/// </summary>
/// <param name = "priceDiscountLinesEnumerator">Enumerator for discount lines.</param>
/// <param name = "_day">Count of the day for which price is being calculated.</param>
private void populatePriceDiscountsOfferIdsWithVariantInTmpTable(ListEnumerator _priceDiscountLinesEnumerator, Day _day)
{
    RetailPriceDiscountLine priceDiscountLine;

    while (_priceDiscountLinesEnumerator.moveNext())
    {
        priceDiscountLine = _priceDiscountLinesEnumerator.current();

        tmpRetailPriceListDisplayValues.AssortmentRecId = priceDiscountLine.parmReferenceRecId();
        tmpRetailPriceListDisplayValues.ChannelRecId = priceDiscountLine.parmChannelRefRecId();
        tmpRetailPriceListDisplayValues.ItemId = priceDiscountLine.parmItemId();
        tmpRetailPriceListDisplayValues.VariantId = priceDiscountLine.parmRetailVariantId();
        tmpRetailPriceListDisplayValues.ActivePrice = priceDiscountLine.parmPrice() - priceDiscountLine.parmDiscount();
        tmpRetailPriceListDisplayValues.Day = _day;

        listOfDisplayDetails.add(tmpRetailPriceListDisplayValues);
    }
}

