public Str60 getDiscountPriceForDay(RetailProductParentView _ppv, str days)
{
    TmpRetailPriceListDisplayValues tmpRetailPriceListDisplayValuesLoc;
    RetailAssortmentLookup          localAssortmentLookup;
    InventTable                     localInventTable;
    RetailChannelTable              localChannelTable;
    InventDim                       localInventDim;
    InventDimCombination            localInventDimCombination;
    int                             daysLocal;
    Price                           calculatedPrice, calculatedPricePre;
    Str60                           returnPrice;

    localAssortmentLookup = _ppv.joinChild();
    localChannelTable = _ppv.joinChild().joinChild().joinChild().joinChild();
    localInventTable = localChannelTable.joinChild();
    localInventDimCombination = localInventTable.joinChild();
    localInventDim = localInventDimCombination.joinChild();

    daysLocal = str2Int(days);

    tmpRetailPriceListDisplayValuesLoc.linkPhysicalTableInstance(tmpRetailPriceListDisplayValues);

    select firstonly tmpRetailPriceListDisplayValuesLoc
        where tmpRetailPriceListDisplayValuesLoc.ItemId == localInventTable.ItemId
        && tmpRetailPriceListDisplayValuesLoc.AssortmentRecId == localAssortmentLookup.RecId
        && tmpRetailPriceListDisplayValuesLoc.ChannelRecId == localChannelTable.RecId
        && tmpRetailPriceListDisplayValuesLoc.VariantId == localInventDimCombination.RetailVariantId
        && tmpRetailPriceListDisplayValuesLoc.Day == daysLocal;

    calculatedPrice = tmpRetailPriceListDisplayValuesLoc.ActivePrice;

    returnPrice = strFmt('%1',calculatedPrice);

    if (params.parmDisplayPriceChangesOnly() && daysLocal > 0)
    {
        select firstonly tmpRetailPriceListDisplayValuesLoc
            where tmpRetailPriceListDisplayValuesLoc.ItemId == localInventTable.ItemId
            && tmpRetailPriceListDisplayValuesLoc.AssortmentRecId == localAssortmentLookup.RecId
            && tmpRetailPriceListDisplayValuesLoc.ChannelRecId == localChannelTable.RecId
            && tmpRetailPriceListDisplayValuesLoc.VariantId == localInventDimCombination.RetailVariantId
            && tmpRetailPriceListDisplayValuesLoc.Day == (daysLocal - 1);

        calculatedPricePre = tmpRetailPriceListDisplayValuesLoc.ActivePrice;
        returnPrice = (calculatedPrice == calculatedPricePre) ? '-' : returnPrice;
    }

    return returnPrice;
}

public display Str60  getInventDimString(RetailProductParentView _retailProductParentView)
{
    InventDim targetDim;
    InventDimCombination dimComb;
    select targetDim join dimComb where targetDim.inventDimId == dimComb.inventDimId && dimComb.DISTINCTPRODUCTVARIANT == _retailProductParentView.Product;

    if (targetDim)
    {
        return targetDim.preFixValue();
    }

    return '';
}

internal display Str60 getProductName(RetailProductParentView _ppv)
{
    Str60 name = EcoResProductTranslation::getNameOrDefaultName(_ppv.ParentProduct, LanguageTable::defaultLanguage());
    return name;
}

/// <summary>
/// Executes the query and fetches data.
/// </summary>
public void executeQuery()
{
    Query generatedQuery;

    super();

    if (!dataGenerated && formTableId)
    {
        generatedQuery = this.query();
        element.calculateDisplayDetailsForCallerOperatingUnit(generatedQuery);

        dataGenerated = true;
    }

}

public display Str60  getInventDimString(InventDim _dim)
{
    if (_dim)
    {
        return _dim.preFixValue();
    }

    return '';
}

/// <summary>
///
/// </summary>
public void clicked()
{
    super();
    Args args = new Args(formstr(RetailPriceListParameterSlider));

    RetailPriceListParameters        paramsToPass;
    paramsToPass = new RetailPriceListParameters("");

    paramsToPass.parmStoreId(element.getCurrentStoreNumber());
    args.record(element.args().record());
    args.parmObject(paramsToPass);

    MenuFunction menuFunction = new MenuFunction(menuitemDisplayStr(RetailPriceListParameterSlider), MenuItemType::Display);

    FormRun formRun = menuFunction.create(args);
    formRun.init();
    formRun.run();
    formRun.wait();

    if (formRun.closedOk())
    {
        params = args.parmObject();
    }
}
